css:
@-moz-document domain("www.nitrotype.com")
{
  body
  {
    background-color: #146B3A;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }


  .card--grit
  {
    background-image: url("https://media.istockphoto.com/photos/christmas-and-new-year-background-with-fir-branches-picture-id1268490748?b=1&k=20&m=1268490748&s=170667a&w=0&h=py3wY8s0zhCmgGJlb73dnV3P9cRDmyOh_4SJbNKPkQw=");
  }


  html
  {
    background: fixed url("https://ae01.alicdn.com/kf/HTB1IMVZXUvrK1RjSszfq6xJNVXac/HUAYI-Snowflake-Christmas-Photography-Backdrop-Xmas-Wood-Background-for-Children-Photo-Studio-Props-Photobooth-XT-7395.jpg") no-repeat, #146B3A;
    box-sizing: border-box;
    color: #146B3A;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 200%;
  }


  .bg--gradient
  {
    background: top left repeat, linear-gradient(90deg, #101010 60%, #146B3A 100%);
  }


  .card--a
  {
    background-image: url("https://wallpaperaccess.com/full/1888832.jpg");
    color: #146B3A;
    background-size: 100%;
  }


  .btn--primary
  {
    background: #146B3A; 
    color: #BB2528;
  }


  .theme--pDefault .profile-title
  {
    color: #146B3A;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }


  .theme--pDefault .profile-levelNum
  {
    color: #146B3A;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }


  .theme--pDefault .profile-body
  {
    color: #146B3A;
  }
}


@-moz-document domain(nitrotype.com)
{


  .dash-copyContainer
  {
    background: #146B3A;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }


  .dash-copy
  {
    color: #BB2528 Of Race Text;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }


  ::selection
  {
    background-color: #165B33;
    color: white;
  }


  .bg--gradient
  {
    background: url(https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSQIAlwzZv7IoyWbtpu7Gra5F8ptMJMt0gx9A&usqp=CAU) no-repeat;
  }


  .theme--pDefault .profile-bg
  {
    background: url("https://media.distractify.com/brand-img/lQbbuK9qm/0x0/zoom-backgrounds-for-christmas-1608661248249.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #146B3A;
  }


  .btn--inky, .btn--inky:focus:not(:hover)
  {
    color: #146B3A;
  }


  .theme--pDefault .profile-bgHead
  {
    background: url("https://media.distractify.com/brand-img/lQbbuK9qm/0x0/zoom-backgrounds-for-christmas-1608661248249.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }


  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://media.distractify.com/brand-img/lQbbuK9qm/0x0/zoom-backgrounds-for-christmas-1608661248249.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }


  .theme--pDefault.is-guest .profile-bgHead
  {
    background: #146B3A;
  }
}
