css:
@-moz-document url-prefix("https://www.rtings.com/") {
body, .header, .test_group,.discussion_list-item-main, .product_page-header-video, .silo_menu:hover .silo_menu-selection, .silo_menu-items, .silo_menu-silo .e-insider_lock, .searchbar_results, .nav_details-aside {
    background-color: #161616 !important;
}

.product_page-banner-scorecard .scorecard, .price_box-box, .landing_page-general_activity, .header-login, .header-login:hover, .landing_page-silo-title:hover, .navbar-title:hover, .searchbar.is-focused .searchbar-input, .searchbar.is-focused .searchbar-input, .scrolling_carousel-left, .scrolling_carousel-right, .navbar-button:hover, .nav_details-content, .nav_details-tab.is-hovered, .index_card, .e-user_badge, select, .product_page-banner-scorecard {
	background-color: #202020 !important;
}

.product_page-test_results, .landing_page-silo {
    background-color: #121212;
}

.nav_details-content {
	border: 2px solid #121212;
}

ol#toc li.heading, .product_page-banner-header, .price_box-store_name {
    color: #585858;
}

.app, .LEGACY, .searchbar.is-focused .searchbar-input, .tiles-group .tiles .tile-medium a, .tiles-group .tiles .tile-small a, .nav_details-tab, .nav_details, .nav_details-aside-content .t-link {
    color: #A0A0A0;
}

.landing_page-insider {
    background-color: rgb(18 18 18 / 0.9);
    color: #585858;
}

.landing_page-banner, .landing_page-silo-title, .best_bar, .best_bar-page, .landing_page-about-title-wrap h2, .landing_page-about-content-text, .landing_page-about-content-text h3, .footer-logo, .footer-item.is-em>a, .landing_page-insider-button, .header-insider, .header-signup, .navbar-title, .navbar-img, .navbar-button-text {
    color: #DFDFDF;
}

.landing_page-banner-background {
    filter: brightness(0.75);
}

.silo_page-recommended {
    background-color: #252525;
}

.deal_card-card {
    background-color: #252525;
}

.deal_card-original-amount {
    color: #A0A0A0;
}

.landing_page-silos {
    background-color: #252525;
}

.landing_page-silos-card {
    background-color: #161616;
}
}
