@-moz-document domain("wikihow.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.toc_header
{
	text-transform: capitalize!important;
}

#hp_categories li,
#mw-mf-page-center .search,

.checkmark,
.hp_thumb,
.related-wh-quiz,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

#header_logo {
    display: inline-block!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.qa_q_txt,
.sample h3 span.mw-headline,
.section h2 span.mw-headline
{
	font-size: 2.5rem!important;
}

#intro p,

.qa_answers,
.steps_list_2 li,
.tips li div,
.warnings li div,

p
{
	font-size: 1.5rem!important;
}

selector
{
	font-size: 1.2rem!important;
}

selector
{
	line-height: 2.5rem!important;
}

.stable .content .responsive_thumb p
{
	line-height: 2.1rem!important;
}

#sidebar .sidebox p {
    line-height: 23px;
}
/* Remove Items 
_____________________________________________________________________________________________________*/

#article_courses_banner,
#hp_special_message,
#intro_ad_1,
#promopair_container,

.wh_ad_inner.rr_container.wh_ad_active,
.steps .wh_ad_inner,
.w_label:before
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

#header_logo {
    display: inline-block!important;
    margin-top: 21px!important;
}

#hp_special_message {
    margin-left: -10rem!important;
}

.quiz_button {
    bottom: -2.25em;
}

#sidebar .ur_review_text {
    margin-top: 21px;
}

.section.sticky > .headline_container.sticking {
    top: 59px;
}

/* Margins 
_____________________________________________________________________________________________________*/

#content_inner,
#hp_container_outer,
#hp_contribute,
#hp_languages,
#hp_news,
#hp_newsletter,
#hp_trustworthy,
#sidebar,

.section_text,
.steps_list_2 li li
{
	margin: 1.2rem!important;
}

selector
{
	margin: 3px 6px!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

#content_inner,
#hp_container_outer,
#hp_news,
#hp_trustworthy,
#sidebar
{
	padding: 1.2rem!important;
}


.qa_question_tab
{
	padding: 3px 12px!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

selector
{
	max-height: none!important;
}

selector
{
	height: auto!important;
}

.header_container.shrunk {
    height: 60px!important;
}

.article_byline {
    height: 4rem;
}


}

@-moz-document domain("wikihow.com") {
