#hp_contribute #hp_contribute_container a,
#hp_newsletter_container a,
#hp_special_message a,

.hp_narrow a.hp_button
{	
	background: radial-gradient(circle, #0f79afa8 0%, #000 100%);
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

