/* ==UserStyle==
@name           GolangTour-Dark
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A dark theme for the "Tour of Go" introduction for the Go programming language
@homepageURL    https://github.com/d-Rickyy-b/GolangTour-Dark
@updateURL      https://raw.githubusercontent.com/d-Rickyy-b/GolangTour-Dark/main/golangtour-dark.user.css
@license        MIT
@author         d-Rickyy-b
==/UserStyle== */

@-moz-document domain("tour.golang.org") {

    /* Overview site at https://tour.golang.org/list */
    .container {
        background-color: #1f2c36;
        color: #c9d1d9;
    }

    .lesson {
        background-color: #1a1f25;
        color: #c9d1d9;
    }

    /* All other sites on https://tour.golang.org/ */
    /* Table of contents (top right) */
    .toc {
        color: #c9d1d9;
        background: #1a1f25;
        border-left: 4px solid#1a1f25;
        border-bottom: 4px solid #1a1f25;
    }

    .toc-lesson {
        background-color: #1f2c36;
        color: #c9d1d9;
    }

    .toc-module {
        color: #c9d1d9;
        background: #1a1f25;
    }

    .toc-page {
        background-color: #263540;
        color: #c9d1d9;
    }

    /* Menu bars */
    .top-bar {
        color: #c9d1d9;
        background: #1a1f25;
    }
    
    #file-menu,
    #explorer {
        color: #c9d1d9;
        background: #1c2831;        
    }
    
    .module-bar {
        color: #c9d1d9;        
    }

    .menu-button,
    .menu-button.active {
        background-color: #3f4954;
        color: #d3d3d3;
    }

    .menu-button:hover:not(.active) {
        border: 1px solid #7e7e7e;
        background-color: #4c5a69;
        color: #d3d3d3;
    }

    /* Left side content*/
    .slide-content {
        background: #1f2c36;
        color: #c9d1d9;
    }

    /* Code blocks left side */
    #left-side pre,
    #left-side code {
        background-color: rgba(110, 118, 129, 0.4);
        color: #fff;
        border-radius: 6px;
        padding-top: 2px;
        padding-bottom: 2px;
        padding-left: 4px;
        padding-right: 4px;
        margin: 2px;
    }

    h1,
    h2,
    h3,
    h4,
    .module-title {
        color: #c9d1d9;
    }

    /* Code Editor division gutter between line numbers and code */
    .CodeMirror-gutters {
        border-right: 1px solid #0000;
    }

    /* Background grid dividing left side from right side and right top from right bottom */
    div[vertical-slide],
    div[horizontal-slide] {
        background: #1a1f25;
    }

    /* Background for code editor and output */
    #file-editor .CodeMirror-lines,
    #file-editor .CodeMirror-gutters,
    #file-editor .CodeMirror,
    #bottom-part,
    .output,
    .output>pre {
        background-color: #1f2c36;
        color: #c9d1d9;
    }

    /* Background for all the root containers */
    html,
    body,
    #editor-container,
    #right-side,
    #file-editor {
        background: #1f2c36;
    }

    /* Text selection in code editor */
    .CodeMirror-selected,
    .CodeMirror-focused .CodeMirror-selected,
    .CodeMirror-line::selection,
    .CodeMirror-line>span::selection,
    .CodeMirror-line>span>span::selection,
    .CodeMirror-line::-moz-selection,
    .CodeMirror-line>span::-moz-selection,
    .CodeMirror-line>span>span::-moz-selection {
        background: #3a3d41;
    }

    /* Code editor cursor/caret color */
    .CodeMirror-cursor {
        border-color: #c9d1d9;
    }

    /*Syntax highlighting*/
    .cm-s-default .cm-keyword {
        color: #548BE1
    }

    .cm-s-default .cm-string {
        color: #ce9178;
    }

    .cm-s-default .cm-atom {
        color: #dcdcaa;
    }

    .cm-s-default .cm-comment {
        color: #6a9949;
    }

    .cm-s-default .cm-number {
        color: #b5cea8;
    }
}
