css:
@-moz-document url-prefix("https://www.brick-hill.com") {
body, html {
    background-color: #071b2f;
    background-position: center top;
    margin: auto;
    margin-top: auto;
    margin-top: 10px;
    width: 1050px;
}

nav div.primary {
    background-color: transparent;
    background-image: url(https://web.archive.org/web/20171226183040im_/https://www.brick-hill.com/assets/Christmas_Banner.png);
    background-size: 100%;
    padding-top: 18%;
    overflow: hidden;
    background-repeat: no-repeat;
}

nav div.primary div.grid {
    background-color: #910f0f;
    margin-top: 1px;
}

nav div.secondary {
    overflow: auto;
    background-color: #267b23;
    color: #fff;
    font-size: .9em;
    padding-top: 0;
}

nav .info {
    border-radius: 2px;
    padding: 5px 10px 15px;
    background-color: #910f0f;
    display: inline-block;
    border: transparent;;
    width: 179px;
    top: 70px;
    position: absolute;
    right: 80px;
}

.bottom-bar {
    background-color: #267b23;
}

.card {
	box-shadow: 0 0px 0px rgb(0 0 0 / 20%);
    border-radius: 2px;
}

.card .content {
	background: transparent;
}

.card .top {
	background: transparent!important;
    border: transparent!important;
    color: #fff!important;
    text-transform: uppercase;
}

.select, select {
	border-radius: 2px;
    background: #071b2f;
    border-color: #fff;
    color: #fff;
}

button:not(.plain):not([disabled]).blue, .button:not(.plain):not([disabled]).blue, button:not(.plain):not([disabled]).free, .button:not(.plain):not([disabled]).free, .input-group .input-button {
    background: linear-gradient(0deg,#b91313 0%,#e71818 100%);
    border: transparent;
    border-radius: 2px;
    font-weight: 600;
}

button:not(.plain):not([disabled]).red, .button:not(.plain):not([disabled]).red {
    background: linear-gradient(0deg,#bd0d15 0%,#e82527 100%);
    border: transparent;
    border-radius: 2px;
    font-weight: 600;
}

button:not(.plain):not([disabled]).orange, .button:not(.plain):not([disabled]).orange, button:not(.plain):not([disabled]).bits, .button:not(.plain):not([disabled]).bits {
	background: linear-gradient(0deg,#F39B00 0%,#FFC200 100%);
    border: transparent;
    border-radius: 2px;
    font-weight: 600;
}

button:not(.plain):not([disabled]).green, .button:not(.plain):not([disabled]).green, button:not(.plain):not([disabled]).bucks, .button:not(.plain):not([disabled]).bucks {
	background: linear-gradient(0deg,#00832C 0%,#00AD39 100%);
    border: transparent;
    border-radius: 2px;
    font-weight: 600;
}

.modal .cancel-button, button:not(.plain), .button:not(.plain) {
    background: transparent;
	border-radius: 2px;
    border-color: #fff;
    color: #fff;
}

input, textarea {
	border-radius: 2px;
    background: transparent;
    border-color: #fff;
}

.input-group {
	border: transparent;
    border-radius: 2px;
    overflow: hidden;
}

input.rigid, textarea.rigid {
	border-color: #fff;
    background: transparent;
    color: #fff;
}

.alert {
	border-radius: 2px;
}

.alert.warning {
	background: linear-gradient(0deg,#F39B00 0%,#FFC200 100%);
}

.alert.success {
	background: linear-gradient(0deg,#00832C 0%,#00AD39 100%);
}

.alert.error {
	background: linear-gradient(0deg,#bd0d15 0%,#e82527 100%);
}

.card .item-card-details, .card .item-content {
	background: transparent;
}

.card .thumbnail.dark, .theme-dark .item.filled[data-v-b18051c0] {
    background: radial-gradient(circle,#41382E 0%,#071b2f 75%);
    border-radius: 2px;
    border: 1px solid #fff;
}

.card .thumbnail.dark:hover {
	border-color: #00a9fe;
}

.tabs .tab-body {
	background: transparent;
    border: transparent;
}

.tabs .tab {
	background: transparent;
    color: #fff;
    border: transparent;
}

.tabs .tab.active {
	background: linear-gradient(0deg,#007CDC 0%,#00A9FE 100%);
    border-radius: 2px;
    color: #fff;
    border: transparent;
}

.avatar-card.crate .img-holder {
	background: radial-gradient(circle,#BCA285 0%,#D6D6DB 75%);
    border-radius: 2px;
    border-color: #fff;
}

.item-card-container {
	background: transparent!important;
    box-shadow: 0 0px 0px rgb(0 0 0 / 20%);
}

.toggle-user-desc:not(.open)::after, .read-more-desc {
	background: #071b2f;
}

.crate-types .active, .theme-dark .new-theme .vertical-tabs li.active {
	background: linear-gradient(0deg,#b91313 0%,#e71818 100%);
    border: transparent;
    border-radius: 2px;
    color: #fff;
}

.crate-types :hover, .profile-card:hover, .theme-dark .new-theme .vertical-tabs li:hover {
    background-color: #383838;
    border: transparent;
    border-radius: 2px;
}

.profile-card.crate img, .profile-card.award img, .avatar-card.crate .img-holder, .item-card-image img {
	background: radial-gradient(circle,#41382E 0%,#071b2f 75%);
    border: 1px solid #fff;
    border-radius: 2px;
}

.trade-picker {
	background: transparent;
}

.trade-picker .trade.selected {
	background: linear-gradient(0deg,#007CDC 0%,#00A9FE 100%);
    border: transparent;
    border-radius: 2px;
    color: #fff;
}

.trade-picker .trade.selected .dark-gray-text {
	color: #fff;
}

hr, .line {
	border: transparent;
    border-top: 1px solid #fff;
}

.side-ad {
	display: none
}

.modal .modal-content {
	background: #071b2f
}

.dropdown-content {
    left: 84%!important;
}

.crate-types {
	border-right: 1px solid #fff;
}
}

@-moz-document url-prefix("https://www.brick-hill.com/shop") {
.col-1-6, .col-2-12 button:not(.plain):not([disabled]).blue, .button:not(.plain):not([disabled]).blue, .col-2-12  button:not(.plain):not([disabled]).green, .button:not(.plain):not([disabled]).green {
    padding: 8px;
}

.box.shaded {
	background: radial-gradient(circle,#41382E 0%,#071b2f 75%);
    border-radius: 2px;
    border-color: #fff;
}
}

@-moz-document url-prefix("https://www.brick-hill.com/play"), url-prefix("https://www.brick-hill.com/sets") {
.card .footer {
	background: transparent;
    border-color: #fff;
}
}

@-moz-document url-prefix("https://www.brick-hill.com/forum") {
.card .topic div.stat {
	font-size: 0.85rem;
}

.card .top:not(.absolute), .card .top:not(.absolute).blue, .card .top:not(.absolute).green, .card .top:not(.absolute).yellow, .card .top:not(.absolute).red {
	text-transform: none!important;
}
}
