css:
@-moz-document url("https://correo.ucr.ac.cr/"), domain("correo.ucr.ac.cr") {
body{
    background-color:#212529 !important;
}
#layout-content{
    background-color:#212529 !important;
}
#layout-content .iframe-wrapper{
    background-color:#212529 !important;
}
#layout>div>.footer{
	background-color:#212529 !important;
}
#layout>div>.header a.button{
	background-color:#212529 !important;
}
#layout>div>.header{
	background-color:#212529 !important;
}
.menu.toolbar a{
	background-color:#212529 !important;
}
body.task-mail.action-preview{
	color:#f1f3f4 !important;
}
.searchbar input{
	background-color:#212529 !important;
}
.searchbar {
    height: 36px;
    min-height: 36px;
    line-height: 36px;
    background-color: #212529;
    border-bottom: 1px solid #343a40;
    display: flex;
    align-items: center;
    overflow: hidden;
    position: relative;
}
#layout>div>.header{
	color:#ffffff !important;
}
.menu.toolbar{
	background-color:#212529 !important;
}
.menu.toolbar a {
    color: #ffffff;
    display: block;
    float: left;
    border: 0 !important;
    height: 58px;
    min-width: 3.2rem;
    max-width: 6rem;
    width: auto;
    padding: .45rem;
    line-height: 1.5;
    cursor: pointer;
    font-size: 1rem;
    text-align: center;
}
#layout>div>.header a.button {
    color: #ffffff;
}
#layout-sidebar {
    position: relative;
    display: flex;
    flex-direction: column;
    flex: 2;
    max-width: 30%;
    min-width: 220px;
    border-right: 1px solid #343a40;
    background-color: #212529;
}
#layout-list {
    position: relative;
    display: flex;
    flex-direction: column;
    flex: 3;
    max-width: 30%;
    min-width: 300px;
    border-right: 1px solid #343a40;
    background-color: #212529;
}
#layout>div>.header {
    border-bottom: 1px solid #212529;
    color: #212529;
    background-color: #212529;
}
.listing tbody td, .listing li a {
    padding: 0 .5rem;
    white-space: nowrap;
    vertical-align: middle;
    color: #f1f3f4;
}
.folderlist li.mailbox .unreadcount {
    position: absolute;
    top: 0;
    right: 0;
    min-width: 2em;
    line-height: 1.4rem;
    margin: 7.7px;
    padding: 0 .3em;
    border-radius: .4em;
    background: #37beff;
    color: #212529;
    text-align: center;
    font-weight: bold;
}
.listing tbody td, .listing li {
    border-bottom: 1px solid #2c363a;
    cursor: default;
    font-weight: normal;
    line-height: 35px;
}
ul.treelist li.selected>div>a, ul.treelist li.selected>a {
    color: #f1f3f4;
    background-color: #2c363a;
}
html:not(.touch) .listing li>a, html:not(.touch) .listing tbody tr>td:first-child, html:not(.touch) .listing:not(.withselection) tbody tr>td.selection+td {
    border-left: 2px solid #343a40;
}
.messagelist td.subject span.fromto {
    overflow: hidden;
    text-overflow: ellipsis;
    flex: 1;
    font-size: 90%;
    color: #f4f4f4;
    padding-left: 1.5em;
    padding-right: .5rem;
}
.messagelist td.subject span.date {
    font-size: 90%;
    color: #f8f9fa;
}
.listing tbody td a {
    color: #f8f9fa;
}
#layout-menu {
    background-color: #212529;
}
body:before {
    background: #212529;
}
.listing li.selected, .listing tr.selected td {
    color: #2c363a;
    background-color: #2c363a;
}
.popover {
    position: absolute;
    top: 0;
    left: 0;
    z-index: 1060;
    display: block;
    max-width: 276px;
    font-family: -apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,"Helvetica Neue",Arial,"Noto Sans",sans-serif,"Apple Color Emoji","Segoe UI Emoji","Segoe UI Symbol","Noto Color Emoji";
    font-style: normal;
    font-weight: 400;
    line-height: 1.5;
    text-align: left;
    text-align: start;
    text-decoration: none;
    text-shadow: none;
    text-transform: none;
    letter-spacing: normal;
    word-break: normal;
    word-spacing: normal;
    white-space: normal;
    line-break: auto;
    font-size: .875rem;
    word-wrap: break-word;
    background-color: #212529;
    background-clip: padding-box;
    border: 1px solid #343a40;
    border-radius: .3rem;
}
.popover {
    box-shadow: 3px 3px 5px #2c363a;
    border-color: #343a40;
    padding: 0;
}
.ui-dialog .ui-dialog-titlebar-close {
    border: 0;
    color: #f4f4f4;
    background: transparent;
    right: 0;
    top: 0;
    position: absolute;
    padding: .25rem .5rem;
    margin: 15px .5rem;
}
.ui-dialog .ui-dialog-title {
    line-height: 58px;
    font-size: 1.25rem;
    padding: 0 3rem 0 1rem;
    color: #f1f3f4;
}
.ui-widget-header {
    font-weight: bold;
    background-color: #212529;
}
.ui-dialog .ui-dialog-titlebar {
    height: 58px;
    border-bottom: 1px solid #212529;
}
body.task-mail.action-none {
    min-width: 240px;
    height: 100%;
    color: #fff;
    overflow: hidden;
}
.ui-dialog .ui-dialog-buttonpane {
    text-align: right;
    white-space: nowrap;
    background-image: none;
    padding: 0 1.5rem;
    height: 4rem;
}
.form-control {
    color: #212529;
}
.custom-select {
    display: inline-block;
    width: 100%;
    height: calc(1.5em + .75rem + 2px);
    padding: .375rem 1.75rem .375rem .75rem;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: #fff;
    vertical-align: middle;
    background: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 4 5'%3e%3cpath fill='%23343a40' d='M2 0L0 2h4zm0 5L0 3h4z'/%3e%3c/svg%3e") no-repeat right .75rem center/8px 10px;
    background-color: #212529;
    border: 1px solid #212529;
    border-radius: .25rem;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
}
.ui-widget-content {
    background-color: #212529;
}
.ui-widget {
    border: 1px solid #2c363a;
    box-shadow: 3px 3px 5px #2c363a;
    border-radius: .3rem;
}
#layout-menu .popover-header {
    padding: 0 !important;
    background-color: #212529 !important;
}
#message-htmlpart1 div.rcmBody .v1wrapper {
    width: 100%;
    table-layout: fixed;
    background: #212529;
    padding-bottom: 3rem;
    color: #000;
}
element.style {
    background: #212529;
    margin-bottom: 2rem;
    padding: 20px;
}
#layout>div>.footer {
    border-top: 1px solid #495057;
    background-color: #fff;
}
.menu.pagenav a {
    flex-grow: 1;
    display: inline-block;
    min-width: 2rem !important;
    height: 35px;
    color: #f1f3f4;
}
div.popover-body.webkit-scroller{
    padding: .5rem .75rem;
    color: #f8f9fa;
}
.form-control {
    color: #f8f9fa;
}
.form-control {
    display: block;
    width: 100%;
    height: calc(1.5em + .75rem + 2px);
    padding: .375rem .75rem;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: #fff;
    background-color: #212529;
    background-clip: padding-box;
    border: 1px solid #343a40;
    border-radius: .25rem;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
}
.menu.pagenav .pagenav-text {
    overflow: hidden;
    text-overflow: ellipsis;
    color: #f1f3f4;
    flex-grow: 4;
    font-size: 80%;
}
.menu a:not(.disabled):focus, .menu a:not(.disabled):hover {
    background-color: #343a40;
}
.searchbar a {
    color: #f8f9fa;
}
input:-internal-autofill-selected {
    appearance: menulist-button;
    background-image: none !important;
    background-color: #212529 !important;
    color: #212529 !important;
}
div#remote-objects-message.ui.alert.alert-warning.boxwarning{
	margin: 0;
    margin-bottom: .2rem;
    opacity: .95;
    width: 100%;
    padding: .75em;
    color: #fff;
    border: 1px solid transparent;
    background-color: rgba(55,190,255,0.05);
    display: flex;
    align-items: center;
}
.attachmentslist li .attachment-name {
    overflow: hidden;
    text-overflow: ellipsis;
    color: #64bceb;
}
body {
    min-width: 240px;
    height: 100%;
    color: #41ade7;
    overflow: hidden;
}
.form-control {
    display: block;
    width: 100%;
    height: calc(1.5em + .75rem + 2px);
    padding: .375rem .75rem;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: #fff;
    background-color: #2c363a;
    background-clip: padding-box;
    border: 1px solid #2c363a;
    border-radius: .25rem;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
}
.mce-container, .mce-container *, .mce-widget, .mce-widget *, .mce-reset {
    margin: 0;
    padding: 0;
    border: 0;
    outline: 0;
    vertical-align: top;
    background: #2c363a;
    text-decoration: none;
    color: #595959;
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    text-shadow: none;
    float: none;
    position: static;
    width: auto;
    height: auto;
    white-space: nowrap;
    cursor: inherit;
    -webkit-tap-highlight-color: #2c363a;
    line-height: normal;
    font-weight: normal;
    text-align: left;
    -moz-box-sizing: content-box;
    -webkit-box-sizing: content-box;
    box-sizing: content-box;
    direction: ltr;
    max-width: none;
}
.mce-tinymce .mce-btn, .mce-tinymce .mce-panel {
    background-color: #2c363a;
}
.mce-btn-group:not(:first-child) {
    border-left: 1px solid #212529;
    padding-left: 0;
    margin-left: 2px;
}
}
