css:
@-moz-document url-prefix("https://sso.uowm.gr/") {
#contentwrapper {
    min-height: 100%;
    position: relative;
    z-index: 1;
    margin: 0 auto -55px;
    height: auto !important;
    background-color: #2e2d2d;
}
#header {
    width: 900px;
    overflow: auto;
    margin: 0 auto;
    padding: 8px;
    z-index: 3;
    color: #CCC;
    border: 1px solid #000;
    box-shadow: 4px 4px 4px #000;
    background: #282828;
    border-bottom: 3px solid #000000;
}
.title {
    color: #ffffff;
    font-family: Arial, Helvetica, sans-serif;
    font-size: 20px;
    font-weight: bold;
    margin-bottom: 7px;
    text-shadow: 2px 4px #000000;
}
.servicetitle {
    color: #fff;
    font-size: 18px;
    font-weight: bold;
    text-shadow: 2px 4px #000000;
}
#main {
    width: 900px;
    margin-left: auto;
    margin-right: auto;
    padding-top: 12px;
    padding-left: 5px;
    padding-right: 5px;
    padding-bottom: 48px;
    height: 100%;
    overflow: auto;
    display: block;
    z-index: 3;
    /* border: 1px solid #CCCCCC; */
    -moz-box-shadow: 0 0 5px #000;
    -webkit-box-shadow: 0 0 5px #000;
    box-shadow: 0 0 5px #000;
    background: #282828;
}
body {
    padding: 0;
    margin: 0;
    font-family:"Trebuchet MS", Arial, Helvetica, sans-serif;
    font-size: 13px;
    color: #9e9e9f;
    height: 100%;
}
h2 {
    color: #9999e5;
    font-size: 15px;
    font-weight: normal;
}
.box-login {
    padding: 0px 15px 15px;
    border: 1px solid #000;
    background: #282828;
    height: 230px;
}
input {
    outline: none;
    transition: all 0.25s ease-in-out;
    -webkit-transition: all 0.25s ease-in-out;
    -moz-transition: all 0.25s ease-in-out;
    border-radius: 3px;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border: 1px solid rgba(0, 0, 0, 0.2);
    font-family:"Trebuchet MS", Arial, Helvetica, sans-serif;
    background-color: #1e1d1d;
    color: white;
}
#nav li.current a {
    background: #428604;
    border-radius: 5px;
    border: 3px solid #055987;
    border-radius: 5px 5px 5px 5px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    background: none repeat scroll 0 0 #282828;
    border: 1px solid #000000;
    color: #055987;
    font-weight: bold;
    margin-left: 0;
    padding: 5px 35px;
}
#nav li.current a:hover {
    background-color: #131413;
}
.box-around {
    margin-left: auto;
    margin-right: auto;
    padding: 12px;
    width: 430px;
    background-color: #282828;
    /* background-color: #F3F2E0; */
    overflow: auto;
}
.box-success {
    margin-left: auto;
    margin-right: auto;
    padding: 12px;
    border: 1px solid #000000;
    width: 400px;
    background-color: #282828;
    box-shadow: 3px 3px 4px #000;
    overflow: auto;
}
}
