/* ==UserStyle==
@name           Jenkins Wide Layout
@description    Removes some wide restriction while displaying pages in jenkins.
@name:de        Jenkins Weites Layout
@description:de Entfernt ein paar Restriktionen bzgl. der Weite bei der Anzeige im Jenkins.
@author         ollily2907
@version        2021-11-24
@license        CC BY-NC - Creative Commons Attribution-NonCommercial
@homepageURL    https://userstyles.org/styles/220201/jenkins-wide-layout
@supportURL     https://userstyles.org/styles/220201/jenkins-wide-layout
@namespace      url(http://www.w3.org/1999/xhtml)
==/UserStyle== */
@-moz-document domain('www.jenkins.io'),
regexp("^http.{0,1}://.+/jenkins(/.+|)/job/.*/configure"),
regexp("^http.{0,1}://localhost(/.+|)/job/.*/configure"),
regexp("^http.{0,1}://localhost:[0-9]{4,5}(/.+|)/job/.*/configure")
{
    #main-panel {
        background-color: #eeeeee !important;
    }

    #main-panel > .container .row {
        border-color: red !important;

    }

    #main-panel > .container .row > div {
        margin-left: -4em !important;
        width: 105% !important;
    }
    #main-panel > .container .row > div > form {
        border-color: #eeeeee !important;
    }
    /* i really want this to be global */
}
