css:
@-moz-document domain(192.168.1.X:81) {
/* NGINX Proxy Mananger Theme */
/* Designed by - FrogMaster */

/* Change Default Avatar*/
.avatar {
    width: 2rem;
    height: 2rem;
    line-height: 2rem;
    border-radius: 50%;
    display: inline-block;
    background: #ced4da no-repeat center/cover;
    position: relative;
    text-align: center;
    color: #868e96;
    font-weight: 600;
    vertical-align: bottom;
    font-size: .875rem;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
  /* Change Avatar Image*/
    background-image: url("data:image/png;base64,") !important
}

/* Header */
.header {
    padding-top: .75rem;
    padding-bottom: .75rem;
    background: #161515b0;
    border-bottom: 1px solid rgba(0,40,100,0.12)
}

/* NGINX Proxy Manager Text */
.navbar-light .navbar-brand {
    color: rgb(115 102 187);
}
/* NGINX Proxy Manager TextHover */ 

.navbar-light .navbar-brand:hover,.navbar-light .navbar-brand:focus {
    color: rgb(86 76 137);
}

/* Header Left Navigation Button IMG*/
.navbar-light .navbar-toggler-icon {
    background-image: url("data:image/svg+xml;charset=utf8,%3Csvg viewBox='0 0 30 30' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath stroke='rgba(115, 102, 187, 1)' stroke-width='2' stroke-linecap='round' stroke-miterlimit='10' d='M4 7h22M4 15h22M4 23h22'/%3E%3C/svg%3E")
}

/* Nav Tabs / Text */
.nav-tabs {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    color: #2d5ea3;
    margin: 0 -.75rem
}

/* Nav Tabs / Active UnderBorder */
.nav-tabs .nav-link:hover,.nav-tabs .nav-link:focus {
    border-color: #467fcf #467fcf #467fcf
}

/*Nav Tabs Hover */
.nav-tabs .nav-link:hover:not(.disabled) {
    border-color: #fd9644;
    color: #fd9644
}

/* Navigation Tabs HOSTS Header */
.nav-tabs .nav-link.active,.nav-tabs .nav-item.show .nav-link {
    color: #fd9644;
    background-color: #16151500;
    border-color: #dee2e6 #dee2e6 #f5f7fb;
}

/* Navigation Tabs HOSTS Dropdown Menu */
.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 10rem;
    padding: 0.5rem 0;
    margin: 0.125rem 0 0;
    font-size: 0.9375rem;
    color: #495057;
    text-align: left;
    list-style: none;
    background-color: #212122;
    background-clip: padding-box;
    border-radius: 3px;
}

/* Right Hand Dropdown Menu / Selection Color*/
.dropdown-item:hover,.dropdown-item:focus {
    color: #467fcf;
    text-decoration: none;
    background-color: #fd9644
}

.dropdown-item {
    color: #467fcf;
}

.dropdown-icon {
    color: #467fcf;
    margin-right: .5rem;
    margin-left: -.5rem;
    width: 1em;
    display: inline-block;
    text-align: center;
    vertical-align: -1px
}

.dropdown-menu-arrow:after {
    position: absolute;
    top: -5px;
    left: 12px;
    display: inline-block;
    border-right: 5px solid transparent;
    border-bottom: 5px solid #467fcf;
    border-left: 5px solid transparent;
    content: ''
}

.dropdown-divider {
    height: 0;
    margin: 0.5rem 0;
    overflow: hidden;
    border-top: 1px solid #467fcf
}

/*SSL Certs Help Buttton */
.card-options a {
    margin-left: .5rem;
    color: #f1c40f;
    display: inline-block;
    min-width: 1rem;
}

.card-options a:hover {
    text-decoration: none;
    color: #fd9644
}

.btn-outline-secondary {
    color: #868e96;
    background-color: transparent;
    background-image: none;
    border-color: #f1c40f;
}

.btn-outline-secondary:hover {
    color: #fff;
    background-color: #467fcf;
    border-color: #fd9644
}

/*Proxy Hosts Site Tags*/
.tag {
    font-size: 0.75rem;
    color: #106f7e;
    background-color: #28083c;
    border-radius: 3px;
    padding: 0 .5rem;
    line-height: 2em;
    cursor: default;
    font-weight: 400;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none
}

/* Table Header Text */
.table th,.text-wrap table th {
    color: #467fcf;
    text-transform: uppercase;
    font-size: 0.875rem;
    font-weight: 400;
}

/*Main Body*/ 
body {
    margin: 0;
    font-family: "Source Sans Pro", -apple-system, BlinkMacSystemFont, "Segoe UI", "Helvetica Neue", Arial, sans-serif;
    font-size: 0.9375rem;
    font-weight: 400;
    line-height: 1.5;
    color: #1c5e8d;
    text-align: left;
    background-color: #262525
}

/* Link Hover Color*/
a:hover {
    color: #fd9644
}

/* Interior Containers */
.card {
    position: relative;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-direction: column;
    flex-direction: column;
    min-width: 0;
    word-wrap: break-word;
    background-color: #161515b0;
    background-clip: border-box;
    border: 1px solid rgba(0,40,100,0.12);
    border-radius: 3px
}

/* Audit Log / ViewDetails Button */
.btn-secondary {
    color: #495057;
    background-color: #13243c;
    border-color: rgba(0,40,100,0.12);
    box-shadow: 0 1px 1px 0 rgba(0,0,0,0.05);
}

.btn-secondary:hover {
    color: #495057;
    background-color: #172c4a;
    border-color: rgba(0,20,49,0.12)
}

/*Edit User Window / Menus */
.modal-content {
    position: relative;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-direction: column;
    flex-direction: column;
    width: 100%;
    pointer-events: auto;
    background-color: #262525;
    background-clip: padding-box;
    border: 1px solid rgba(0,0,0,0.2);
    border-radius: 3px;
    outline: 0;
}

.form-control {
    display: block;
    width: 100%;
    padding: 0.375rem 0.75rem;
    font-size: 0.9375rem;
    line-height: 1.6;
    color: #495057;
    background-color: #1c1c1c;
    background-clip: padding-box;
    border: 1px solid rgba(0,40,100,0.12);
    border-radius: 3px;
    transition: border-color 0.15s ease-in-out, box-shadow 0.15s ease-in-out;
}

.form-control:focus {
    color: #495057;
    background-color: #262525;
    border-color: #1991eb;
    outline: 0;
    box-shadow: 0 0 0 2px rgba(70,127,207,0.25);
}

/* Menus / Borders */
.modal-header {
    display: -ms-flexbox;
    display: flex;
    -ms-flex-align: start;
    align-items: flex-start;
    -ms-flex-pack: justify;
    justify-content: space-between;
    padding: 1rem;
    border-bottom: 1px solid #467fcf;
    border-top-left-radius: 3px;
    border-top-right-radius: 3px
}

.modal-footer {
    display: -ms-flexbox;
    display: flex;
    -ms-flex-align: center;
    align-items: center;
    -ms-flex-pack: end;
    justify-content: flex-end;
    padding: 1rem;
    border-top: 1px solid #467fcf
}

.table th,.text-wrap table th,.table td,.text-wrap table td {
    padding: 0.75rem;
    vertical-align: top;
    border-top: 1px solid #467fcf
}

.table thead th,.text-wrap table thead th {
    vertical-align: bottom;
    border-bottom: 2px solid #467fcf
}

.table tbody+tbody,.text-wrap table tbody+tbody {
    border-top: 2px solid #467fcf
}

/* Menus / 404 Host Window*/
.selectize-input,.selectize-control.single .selectize-input.input-active {
    background: #1c1c1c;
    cursor: text;
    display: inline-block;
}

.selectize-control.single {
    display: inline-block;
    cursor: text;
    background: #262525;
}

.selectize-input.full {
    background-color: #161515;
}

.selectize-control.single .selectize-input:after {
    content: '';
    display: block;
    position: absolute;
    top: 13px;
    right: 12px;
    width: 8px;
    height: 10px;
	/* This section is broken but oh well*/
	/* Image doesn't display, meh looks better without */
    /* background: #5eba00 url(data:image/svg+xml;charset=utf8,%3Csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 10 5'%3E%3Cpath fill='#999' d='M0 0L10 0L5 5L0 0'/%3E%3C/svg%3E) no-repeat center; */ 
    background-size: 8px 10px;
    transition: .3s transform;
}

.selectize-dropdown .active {
    color: #495057;
    background-color: #161515
}

/* Toggle Switches */
.custom-switch-indicator {
    display: inline-block;
    height: 1.25rem;
    width: 2.25rem;
    background: #161515;
    border-radius: 50px;
    position: relative;
    vertical-align: bottom;
    border: 1px solid rgba(0,40,100,0.12);
    transition: .3s border-color, .3s background-color;
}

.custom-switch-indicator:before {
    content: '';
    position: absolute;
    height: calc(1.25rem - 4px);
    width: calc(1.25rem - 4px);
    top: 1px;
    left: 1px;
    background: #467fcf;
    border-radius: 50%;
    transition: .3s left;
    box-shadow: 0 1px 2px 0 rgba(0,0,0,0.4)
}

.selectgroup-input:checked+.selectgroup-button {
    border-color: #467fcf;
    z-index: 1;
    color: #467fcf;
    background: #161515
}

/* Remove the annoying footer */
.footer {
    display: none;
}
}
