@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.warez-bb.org") {

/* Links used in the site, user rank colors on main page looks fine with it, thanks DI :) */
a:link:not([style]):not(:hover),a:active,a:visited:not([style]):not(:hover),a.postlink, b > a:link:not([style]):not(:hover), b > a:active:not(:hover), b > a:visited:not(:hover), b > a.postlink:not(:hover){color:#635B53 !important;text-decoration:none; border: 0 !important;}
a:hover, b > a:hover {color:#635B53 !important;}

/* The largest text used in the index page title and topic title etc. */
.maintitle,h1{font:bold 20px/120% "Trebuchet MS",Verdana,Arial,Helvetica,sans-serif;text-decoration:none;color:#635B53 !important}

/* The largest text used in the index page title and topic title etc. PS: Come as guest thingy */
.header_maintitle,h1{font:bold 20px/120% "Trebuchet MS",Verdana,Arial,Helvetica,sans-serif; text-decoration:none; color:#635B53 !important}

/* Outermost area of the forum */ 
body:not(.gaybody){background:#DCFBFE !important;font-size:12px; font-family:Verdana,Arial,Helvetica,sans-serif;margin:6px;padding:0;}

/* Second outermost area of forum. The one used between actual forum area box and outmost area. Well tbh, try changing color to black to see clearly that part of the forum */
.bodyline:not(.gaybodyline){background:#ABEAFC !important;background-repeat:repeat;border:1px solid #313131 !important;}

/* The red rounded rectangle where they show you PM notification. Thanks to glenny for reporting and its fixed now. (: */
table.bodyline > tbody > tr > td > table > tbody > tr > td > div > div > p:not(.gtfodarktheme){ color: #ED9D9E !important; }

/* Advertisement area rounded rectangle and powered by phpBB rounded rectangle */
table.bodyline > tbody > tr > td > table > tbody > tr > td > div:not(.gtfodarktheme) {
	background: #c2cfdd url(http://i37.tinypic.com/m8pf0z.jpg) repeat-x !important;
	border: 1px solid #8a8a8a !important; -moz-border-radius: 0px; }

/* The footer ads rounded rectangle and Time, Queries stats rectangle */
.footerline{background: #c2cfdd url(http://i37.tinypic.com/m8pf0z.jpg) repeat-x !important;background-repeat:repeat;border:1px solid #888 !important;-moz-border-radius: 1px;}

/* This is the gradient background at the top of the page (the logo section)*/
.topbkg/*:not([style*="750k_logo"])*/{background: url(http://i34.tinypic.com/w6t4q9.jpg) repeat-x !important} /*images/cellpic_bkg.png*/

/* The top navigation bar which shows Search, Profile, Logout etc. */
.topnav{font-size:10px;background: #71b9de url(http://i33.tinypic.com/4t4uhs.jpg) repeat-x !important; /*images/cellpic_nav.gif*/
height:21px;white-space:nowrap;border: 0px solid #71b9de !important;border-width: 1px 0px 1px 0px}

/* Guess what, its our logo! You can even change logo position to center by increasing padding */
table.topbkg td a[href*="index.php"] > img:not([src*="750k_logo"]) {
	height: 110px !important;
	width: 0 !important;
	padding-right: 290px !important;
	background: url(http://i33.tinypic.com/vzfntx.jpg) no-repeat right;	
}

/* This is the gradient background at the top of the page. The one where text like Forum, Topics, Posts, Last Post is written.*/
th/*:not([style*="750k_logo"])*/{background: #C7C7C7 url(http://i38.tinypic.com/dghtnb.jpg) !important} /*cellpic3.png*/

/* The style applied to Category name in the forum. e.g. Listing, Reuests. */
td.cat/*:not([style*="750k_logo"])*/{background: #FFFFFF url(http://i37.tinypic.com/pnhjs.jpg) !important} /*cellpic1.png*/

/* The one for the first... umm nvm. Figure out these yourselves. I can't really explain them. :( */
.row1{ background : #CEF3F9 repeat-x !important }
.row2, .helpline{ background : #9FECFF repeat-x !important ;}
.row3 { background : #B5EFFB !important ;}

/* Space row between 2 posts */
td.spacerow{background:#a7d7f0 url(http://i34.tinypic.com/2rxxq94.jpg) !important}

/* Hover effects */

tr[id*="cat_id_"]:hover > td.row1{
	background: url(http://i33.tinypic.com/24bkrbm.jpg) !important;
}
tr[id*="cat_id_"]:hover > td.row2{
	background: #CEF3F9 repeat-x !important;
}
tr[id*="cat_id_"]:hover > td.row3{
	background: url(http://i33.tinypic.com/24bkrbm.jpg) !important;
}

table.forumline + table[width="100%"][cellspacing="2"][cellpadding="2"] + table[width="100%"][cellspacing="2"][cellpadding="2"] + table.forumline[width="100%"][cellspacing="1"][cellpadding="2"] > tbody > tr:not(:first-child):hover > td.row1:not([width="22%"]):not([width="78%"]):not([valign="top"]) {
	background: url(http://i33.tinypic.com/24bkrbm.jpg) !important;
}
