/* Text area glow */

textbox:focus, textarea:focus, input:focus
{
/*  -moz-box-shadow: #DBA4AA 1px 1px 1px;*/
  -moz-box-shadow: #DBA4AA 1px 1px 1px, #863486 1px 1px 1px, #DBA4AA 1px 1px 11px, #DBA4AA 1px 1px 11px;  
}

select:focus, input:not([type="checkbox"]):not([type="radio"]):focus
{
  -moz-appearance: none !important;
}

/* Go button change */

input.button[value="Go"]{background: #ECECEC !important; color:#555555 !important; border: 1px #111111 !important }
input.post[name="search_keywords"]:not([size="30"]):not([style="width: 100px;"]):not([onfocus*="Enter"]){background: #ECECEC !important; color:#6B6B6B !important; border: 1px #111111 !important}

/* Plus minus button */

img[src*="exp_minus.gif"]{
       display: none;
}

a[href$="1);"]:before{
content: url(http://i35.tinypic.com/2vjzs6q.jpg);
