css:
@-moz-document url-prefix("https://discord.com")
{
/* ------BACKGROUND------ */
  body
  {
    background: url("https://www.gifcen.com/wp-content/uploads/2021/10/kuromi-gif-13.gif");
    background-size: cover;
  }

  .appMount-3lHmkl
  {
    background: rgba(255, 165, 238, .6);
  }

/* ------REMOVE GREY/WHITE BG COLORS------ */
  .theme-dark,
.theme-light
  {
    --header-primary: #171717;
    --header-secondary: #171717;
    --text-normal: #171717;
    --text-muted: #171717;
    --channels-default: #171717;
    --interactive-normal: #171717;
    --interactive-hover: #171717;
    --interactive-active: #171717;
    --interactive-muted: #171717;
    --background-primary: transparent;
    --background-secondary: transparent;
    --background-secondary-alt: transparent;
    --background-tertiary: transparent;
    --background-floating: rgba(0, 0, 0, .8);
    --channeltextarea-background: transparent;
    --deprecated-panel-background: transparent;
    --background-modifier-hover: rgba(0, 0, 0, .42);
    --background-modifier-selected: rgba(0, 0, 0, .45);
  }

/* ------POP-UP------ */
  .modal-yWgWj-,
.footer-3rDWdC
  {
    background: rgba(0, 0, 0, .5) !important;
  }


/*remove borders*/
  .header-2o-2hj,
.searchBar-6Kv8R2,
.content-yTz4x3::before
  {
    box-shadow: none !important;
  }

/*misc*/
  .container-1D34oG,  /*friends list*/
.scrollbar-2rkZSL,  /*scrollbar*/
.pad-29zQak,
.track-1JN30G
  {
    background: transparent !important;
  }

/* ------SCROLLBAR------ */
  .scrollbar-2rkZSL
  {
    width: 8px;
  }

  .thumb-2JwNFC
  {
    background: rgba(0, 0, 0, .5) !important;
  }

  .track-1JN30G,
.thumb-2JwNFC
  {
    border: none !important;
  }

/* ------EMBEDDED TEXT------ */
  .embedProvider-3k5pfl,
.embedDescription-1Cuq9a,
.embedFields-2IPs5Z,
.embedAuthorName-3mnTWj,
.embedFooterText-28V_Wb
  {
    color: #171717 !important;
  }

  .cozy-3raOZG .messageContent-2qWWxC, .compact-T3H92H .header-23xsNx .username-1A8OIy, .cozy-3raOZG .header-23xsNx .username-1A8OIy
  {
    text-shadow: 0 0 20px black;
  }
}
