  #navbar-search-input, #navbar-search-btn
  {
    border: 1px solid #1f141e;
    ;
  }

  body
  {
    background: transparent !important;
  }

  .content
  {
    background: transparent !important;
  }

  #fb-root
  {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url(https://i.pinimg.com/originals/2c/b4/96/2cb49653c4594a4cc6b4a949ae2c4415.gif) !important;
      /* Background */
    background-size: cover !important;
    background-attachment: fixed !important;
  }

  #party_none_title, div.friend_dock_chatbox_titlebar.chat-header-blink-off
  {
    background: linear-gradient(to bottom, #e5acb6, #e5acb6);
  }

  #ctl00_cphRoblox_RightColumn, #Body > table > tbody > tr > td:nth-child(3), #Body > table > tbody > tr > td:nth-child(4)
  {
    display: none;
  }

  #GamesPageLeftColumn
  {
    width: auto;
    margin-right: 10px !important;
    margin-left: 10px !important;
  }

  #GamesListsContainer > div
  {
    background-color: transparent !important;
    width: 100% !important;
  }

  #navigation
  {
    background-color: #281e28;
  }

  .notification-red
  {
    background-color: #281e28;
    padding: 1px 6px;
    min-width: 18px;
  }

  .notification-blue
  {
    background-color: #281e28;
    padding: 1px 6px;
    min-width: 18px;
  }

  .rbx-btn-primary-lg
  {
    background-color: rgba(255, 192, 203, 0.5);
  }

  .rbx-btn-primary-lg:hover, .rbx-btn-primary-lg:focus
  {
    background-color: rgba(255, 192, 203, 0.5);
  }

  .avatar-back
  {
    background-image: url(https://drive.google.com/file/d/1oE53p4dnJDHjrA25SKP0j3OzxgaxYA1V/view?usp=sharing);
  }


/* other roblox stuff*/

  .dark-theme .rbx-header
  {
    background-color: #ffffff;
    border: solid #1f141e;
    border-width: 0 0 1px;
  }


/* profile */

  .dark-theme .section-content
  {
    background-color: #1f141e;
    color: #fffff;
  }

  .dark-theme .rbx-tabs-horizontal .nav-tabs
  {
    background-color: transparent !important;
  }

  .dark-theme .rbx-tab .rbx-tab-heading
  {
    background-color: transparent !important;
  }

  .dark-theme .slide-switcher .slide-item-container .slide-item-container-left
  {
    background-color: #1f141e;
  }

  .dark-theme .profile-avatar-right .profile-avatar-mask
  {
    background-color: #1f141e;
  }

  .dark-theme .thumbnail-2d-container
  {
    border: 0;
    background-color: transparent !important;
  }

  .dark-theme .avatar .avatar-card-image
  {
    background-color: #1f141e;
  }

  .dark-theme .avatar-container .avatar-card-image, .dark-theme .avatar-container img
  {
    background-color: #1f141e;
  }

  .dark-theme .layer
  {
    background-color: #1f141e;
  }


/* catalog stuff + buy robux button*/

  .dark-theme .btn-growth-md
  {
    background-color: #9f3e5f;
    border-color: #ffffff;
    color: #fff;
    border-radius: 100px;
  }

  .dark-theme .touch .btn-growth-md.active, .dark-theme .touch .btn-growth-md:active, .dark-theme .touch .btn-growth-md:focus, .dark-theme .touch .btn-growth-md:hover, .dark-theme .touch .btn-growth-md:visited, .light-theme .touch .btn-growth-md.active, .light-theme .touch .btn-growth-md:active, .light-theme .touch .btn-growth-md:focus, .light-theme .touch .btn-growth-md:hover, .light-theme .touch .btn-growth-md:visited
  {
    color: #fff;
    background-color: #9f3e5f;
    border-color: #ffffff;
  }


/* PLAY BUTTON*/


@keyframes rainbow
  {
    0% {background-color: #371c24;
    border-color: #371c24;
  }

  14%
  {
    background-color: #9f3e5f;
    border-color: #9f3e5f;
  }

  28%
  {
    background-color: #371c24;
    border-color: #371c24;
  }

  42%
  {
    background-color: #9f3e5f;
    border-color: #9f3e5f;
  }

  56%
  {
    background-color: #371c24;
    border-color: #371c24;
  }

  80%
  {
    background-color: #9f3e5f;
    border-color: #9f3e5f;
  }

  94%
  {
    background-color: #371c24;
    border-color: #371c24;
  }

  100%
  {
    background-color: #9f3e5f;
    border-color: #9f3e5f;
  }
}

.btn-common-play-game-lg
{
  animation: rainbow 10s infinite;
}


/* BTR */
.dark-theme .rbx-tabs-vertical .category-tabs .menu-vertical
{
  background-color: #1f141e;
}
