css:
@-moz-document domain(etsy.com)
{
.wt-grid {
    display: flex !important;
    flex-wrap: initial;
    flex-direction: column;
}

.wt-display-inline-block {
    display: inline-block !important;
    display: flex !important;
    flex-direction: row;
}

.v2-listing-card__img {
    width: 100px !important;
    margin-right: 20px;
}

.v2-listing-card h3.wt-text-truncate.wt-text-caption {
    font-size: 16px;
}

.wt-grid .wt-grid__item-xl-3 {
    max-width: 100% !important;
}

grid.wt-grid--block [class*="wt-grid__item-"] {
    border: 1px solid gray;
}

.stars-svg {
    display: none !important;
}

.v2-listing-card span.wt-text-body-01.wt-text-gray{
display:none !important;
}

.v2-listing-card .n-listing-card__price p.wt-text-title-01 {
    font-weight: normal !important;
}

h3 {
font-size: 15px;
color: #0F1111;
font-weight: bold;
border: 1px solid #0F1111;
padding: 6px;
}

li {
    border: 1px solid gray;
}
}
