/* ==UserStyle==
// @name         PKU-Art
// @description  给你一个足够好看的教学网
// @version      2.3.21
// @match        *://*.pku.edu.cn/*
// @run-at       document-start
// @author       Arthals
// @license      GPL-3.0 license
// @author-blog  https://arthals.ink
// @namespace    https://github.com/zhuozhiyongde/PKU-Art
// @supportURL   https://github.com/zhuozhiyongde/PKU-Art/issues
// @updateURL    https://cdn.arthals.ink/release/PKU-Art.user.js
// @date         2023/06/05
==/UserStyle== */
@-moz-document regexp("^https:\\/\\/iaaa\\.pku\\.edu\\.cn\\/\\S*$|^https:\\/\\/course\\.pku\\.edu\\.cn\\/\\S*$|^https:\\/\\/livingroomhqy\\.pku\\.edu\\.cn\\/\\S*") {
    
:root {
    --c-title: #212121;
    --c-text: #666;
    --c-border: #d0d7de;
    --c-background: #f6f8fa;
    --c-input-bg: #f6f8fa;
    --c-card: #fff;
    --c-hover: rgba(230, 230, 230, 0.5);
    --c-focus: #fff;
    --c-box-shadow: rgba(0, 0, 0, 0.1) 0px 10px 50px;
    --c-accent: #9b0000;
    --c-navbar: #fff;
    --c-sidebar: #fff;
    --c-subtitle: #82a3ac;
    --c-label: #f6f8fa;
    --c-link: #fcc59f;
    --c-primary: #165dff;
    --c-primary-light: #e8f3ff;
    --c-secondary: #89a2ac;
    --c-button: #f7f8fa;
    --c-card-hover: #66ecf331;
    --c-scrollbar: #c1c1c1;

    /* 图片 */
    --i-logo: url('https://cdn.arthals.ink/css/src/PKU_Logo.svg');
    --i-remind: url('https://cdn.arthals.ink/css/src/remind.svg');
    --i-exit: url('https://cdn.arthals.ink/css/src/exit.svg');
    --i-watch: url('https://cdn.arthals.ink/css/src/stopwatch-start.svg');
    --i-file: url('https://cdn.arthals.ink/css/src/file.svg');
    --i-folder: url('https://cdn.arthals.ink/css/src/folder.svg');
    --i-link: url('https://cdn.arthals.ink/css/src/link.svg');
    --i-setting: url('https://cdn.arthals.ink/css/src/setting.svg');
    --i-check: url('https://cdn.arthals.ink/css/src/check.svg');
    --i-download: url('https://cdn.arthals.ink/css/src/download.svg');

    /* 不需要根据黑暗模式改编的变量 */
    --c-pku: #9b0000;

    --i-alarm: url('https://cdn.arthals.ink/css/src/alarm-clock.svg');
    --i-idcard: url('https://cdn.arthals.ink/css/src/id-card-v.svg');
    --i-tip: url('https://cdn.arthals.ink/css/src/tip.svg');
    --i-comment: url('https://cdn.arthals.ink/css/src/comment.svg');
    --i-clover: url('https://cdn.arthals.ink/css/src/clover.svg');
    --i-sandclock: url('https://cdn.arthals.ink/css/src/sandclock.svg');
}

@media (prefers-color-scheme: dark) {
    :root {
        --c-title: #e2e2e2;
        --c-text: #bababa;
        --c-border: #30363d;
        --c-background: #020409;
        --c-input-bg: #020409;
        --c-hover: rgba(29, 33, 41, 0.5);
        --c-card: #0e1017;
        --c-focus: #010409;
        --c-box-shadow: none;
        --c-accent: #e44c47;
        --c-navbar: #171a22;
        --c-sidebar: #0e1017;
        --c-subtitle: #86909c;
        --c-label: #22252d;
        --c-link: #f9925a;
        --c-primary: #7dc1fc;
        --c-primary-light: #262947;
        --c-secondary: #00879d;
        --c-button: #22262d;
        --c-card-hover: rgba(23, 231, 242, 0.23);
        --c-scrollbar: #6b6b6b;

        --i-logo: url('https://cdn.arthals.ink/css/src/PKU_Logo_Dark.svg');
        --i-remind: url('https://cdn.arthals.ink/css/src/remind_dark.svg');
        --i-exit: url('https://cdn.arthals.ink/css/src/exit_dark.svg');
        --i-watch: url('https://cdn.arthals.ink/css/src/stopwatch-start_dark.svg');
        --i-file: url('https://cdn.arthals.ink/css/src/file_dark.svg');
        --i-folder: url('https://cdn.arthals.ink/css/src/folder_dark.svg');
        --i-link: url('https://cdn.arthals.ink/css/src/link_dark.svg');
        --i-setting: url('https://cdn.arthals.ink/css/src/setting_dark.svg');
        --i-check: url('https://cdn.arthals.ink/css/src/check_dark.svg');
        --i-download: url('https://cdn.arthals.ink/css/src/download_dark.svg');
    }
}

body,
html {
    background-color: var(--c-background);
    /* animation: transparentToOpaque .5s ease-in-out; */
}

* {
    outline: none !important;
}

@keyframes fadeInUpTransition {
    from {
        opacity: 0;
        -webkit-transform: translateY(100px);
        transform: translateY(100px);
    }

    to {
        opacity: 1;
        -webkit-transform: none;
        transform: none;
    }
}

@keyframes fadeInUpStablePixel {
    from {
        opacity: 0;
        -webkit-transform: translateY(200px);
        transform: translateY(200px);
    }

    to {
        opacity: 1;
        -webkit-transform: none;
        transform: none;
    }
}

@keyframes fadeInUpStablePixelForContentBox {
    from {
        margin-left: min(25%, 300px);
        opacity: 0;
        -webkit-transform: translateY(200px);
        transform: translateY(200px);
    }

    to {
        margin-left: min(25%, 300px);
        opacity: 1;
        -webkit-transform: none;
        transform: none;
    }
}

@keyframes fadeInLeftStablePixel {
    from {
        color: #fff;
        transform: translateX(-72px);
        -webkit-transform: translateX(-72px);
    }

    to {
        opacity: 1;
        -webkit-transform: none;
        transform: none;
    }
}

@keyframes tracking-in-expand {
    0% {
        letter-spacing: -0.5em;
        opacity: 0;
    }

    40% {
        opacity: 0.6;
    }

    100% {
        opacity: 1;
    }
}
/* 定义滚动条的宽度和高度 */
::-webkit-scrollbar {
    width: 10px;
    height: 10px;
}

/* 定义滚动条轨道的背景色 */
::-webkit-scrollbar-track {
    background-color: transparent;
}
::-webkit-scrollbar {
    width: 5px;
}

/* 定义滚动条滑块的背景色 */
::-webkit-scrollbar-thumb {
    background-color: var(--c-scrollbar);
    border-radius: 9999px;
}

}
