/* 动画 */
#contentPanel {
    -webkit-animation: fadeInUpStablePixel 0.7s cubic-bezier(0.215, 0.61, 0.355, 1) both;
    animation-delay: 500ms;
    animation: fadeInUpStablePixel 0.7s cubic-bezier(0.215, 0.61, 0.355, 1) both;
}

.localViewToggle {
    display: none;
}

#pageTitleHeader {
    /*color: var(--c-primary);*/
    /*background-color: var(--c-primary-light);*/
    color: var(--blue-7) !important;
    background-color: var(--blue-2);
    padding: 0.3em 1em;
    border-radius: 5px;
    font-size: 20px !important;
}

#pageTitleHeader * {
    color: var(--blue-7) !important;
}

#pageTitleText {
    font-weight: bold;
}

/* 个人导航面板 */
#vertical_container,
#bottomButtons {
    display: none;
}

#global-nav-tools {
    z-index: 9999;
}

#global-nav-flyout {
    box-shadow: none;
    border-radius: 5px;
    width: 40px !important;
    height: 100px !important;
    transform: translateY(34px);
    background-color: transparent;
    /*background-color: #000;*/
}

#global-nav-tools {
    background-color: var(--c-card);
}

#global-list-tools {
    margin: 0;
    box-shadow: var(--c-box-shadow);
    border: 1px solid var(--c-border);
    padding: 5px;
    border-radius: 5px;
    background-color: var(--c-card);
    /*border:1px solid #EEE;*/
}

#global-list-tools > .overview,
#global-list-tools > .stream,
#global-list-tools > .calendar {
    display: none;
}

#global-list-tools > .alerts,
#global-list-tools > .grade {
    background-color: var(--c-card);
}

#global-list-tools > .alerts > a:hover,
#global-list-tools > .grades > a:hover {
    background-color: var(--c-hover);
}

#global-list-tools > .alerts > a {
    border-radius: 5px;
    background: var(--i-alarm);
    /* background-color: var(--c-card) !important; */
    background-size: 60%;
    background-repeat: no-repeat;
    background-position: center center;
    margin: 0 auto;
    /* transition: 350ms opacity; */
}

#global-list-tools > .grades > a {
    border-radius: 5px;
    background: var(--i-idcard);
    /* background-color: var(--c-card) !important; */
    background-size: 60%;
    background-repeat: no-repeat;
    background-position: center center;
    margin: 0 auto;
    /* transition: 350ms opacity; */
}

.mybb-tools li a {
    opacity: 1;
    background-color: var(--c-card);
}

.mybb-tools li a:focus,
.mybb-tools li a:focus img {
    outline: none;
}

}
@-moz-document regexp("^https:\\/\\/course\\.pku\\.edu\\.cn\\/webapps\\/\\S*announcement\\S*context=mybb\\S*$|^https:\\/\\/course\\.pku\\.edu\\.cn\\/webapps\\/blackboard\\/execute\\/announcement") {
    
.announcementFilter,
.announcementList {
    margin: 0;
}

#containerdiv {
    margin-top: 0;
    padding-top: 0;
}

.announcementFilter {
    display: flex;
    align-items: center;
    justify-content: center;
    /* border-bottom: 1px solid var(--c-border); */
    border-bottom: none;
    margin-bottom: 10px;
}


.announcementFilter select#searchSelectId {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    background: var(--c-card);
    color: var(--c-text);
    border: 1px solid var(--c-border);
    border-right: none;
    border-radius: 5px 0 0 5px;
    outline: none;
    padding: 3px 10px;
    box-sizing: border-box;
    height: 30px;
    margin: 0;
}

.announcementFilter a.genericButton {
    background: var(--c-button);
    color: var(--c-title);
    border: 1px solid var(--c-border);
    border-left: none;
    border-radius: 0 5px 5px 0;
    outline: none;
    padding: 3px 10px;
    box-sizing: border-box;
    height: 30px;
    box-shadow: none;
    /* margin-left: 20px; */
}

.announcementFilter a.genericButton:hover {
    background: var(--cyan-2);
    color: var(--cyan-6);
}
}
@-moz-document regexp("^https:\\/\\/course\\.pku\\.edu\\.cn\\/webapps\\/\\S*playVideo\\S*") {
    
/* 容器 */
#content,
.container {
    border: none;
