@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("thesixtyone.com") {

/********************************** ads */
#vertical_ad_panel, .horizontal_blah { display: none !important; }

/******************************* layout */
/*************** top toolbar **/
#top_toolbar {
  left: 0 !important;
  background: #666 !important;
  width: 100% !important;
}

#top_toolbar > tbody > tr {
  display: block !important;
  width: 930px !important;
  margin: 0 auto !important;
}

#top_toolbar > tbody > tr > td:first-of-type {
  width: 100% !important;
}

#top_toolbar > tbody > tr > td:last-of-type {
  display: none !important;
  width: 0 !important;
  overflow: hidden !important;
}

.top_toolbar_filler {
  width: auto !important;
}

/************ main containers */
#page_content {
  padding: 0 !important;
  margin:12px 0 8px 6px !important;
}

#page_layout {
  width: 926px !important;
  padding: 4px !important;
}

#bottom_navigation {
  width: 912px !important;
}

#back_button_container {
  top: auto !important;
  bottom: 2px !important;
  width: 60px !important;
  height: 25px !important;
}

#feedback {
  opacity: 0 !important;
}

#feedback:hover {
  opacity: 1 !important;
}

/**************** left column */
.subnavigation a.selected, .subnavigation a.selected:hover {
  background: moz-linear-gradient(left, right, from(#e0e0e0), to(#fff)) !important;
    background: -webkit-gradient(linear, left, right, from(#e0e0e0), to(#fff)) !important;
}

.subnavigation a.subnav_button:hover {
  background: -moz-linear-gradient(left, right, from(#f2f2f2), to(#fff)) !important;
  background: -webkit-gradient(linear, left, right, from(#f2f2f2), to(#fff)) !important;
}

/*************** right column */
.large_button a:hover {
  background: CornFlowerBlue !important;
}

.select_button a.selected:link, .select_button a.selected:visited {
  background: CornFlowerBlue !important;
}

/****************************** textual */


}
