css:
@-moz-document domain("gamepedia.com"), domain("fandom.com"), domain("wikia.com") {
.global-navigation {
    display: none;
}
.resizable-container {
    max-width: 2000px;
}
.main-container {
    margin-left: 0;
    width: 100%;
}
.mcf-wrapper {
    display: none;
}
.page__right-rail {
    display: none;
}
.fandom-sticky-header {
 	left:0; 
}
/*Taken from cheatfreak47
https://userstyles.org/styles/134432/wikia-fandom-uncluttered-and-unbranded */

/*hide stupid wikia bar on the bottom of the page*/
 .WikiaBarWrapper, .WikiaBarCollapseWrapper {
    display: none;
}
/*hide absurdly huge footer section on every page*/
 .global-footer {
    display: none;
}
/*hide "Also on Fandom!" Bar*/
 .WikiaFooter, .WikiaBarWrapper section {
    display: none;
}
/*Hide "wikia rail" on article pages*/
 .WikiaRail {
    display: none;
}
.WikiaMainContent {
    width: 100%;
}
}
