    padding: 10px 15px;
    margin-bottom: 10px;
}
.titleBar .prefix {
    font-size: inherit;
    line-height: 30px;
    border-radius: 0px;
}
.pollResult .bar {
    background: rgb(34,142,93);
        background-color: rgb(34, 142, 93);
        background-position-x: 0%;
        background-position-y: 0%;
        background-repeat: repeat;
        background-attachment: scroll;
        background-image: none;
        background-size: auto;
        background-origin: padding-box;
        background-clip: border-box;
    height: 100%;
    display: block;
    zoom: 1;
    border-radius: 0;
}
.tagList .tag {
    font-weight: 600;
    position: relative;
    display: inline-block;
    background: rgb(54,54,54);
        background-color: rgb(54, 54, 54);
        background-position-x: 0%;
        background-position-y: 0%;
        background-repeat: repeat;
        background-attachment: scroll;
        background-image: none;
        background-size: auto;
        background-origin: padding-box;
        background-clip: border-box;
    margin-left: 4px;
    height: 26px;
    line-height: 26px;
    padding: 0 9px;
    border-radius: 0;
    margin-bottom: 2px;
}
.tabs li.active {
    background: #228e5d;
}
.marketMyPayments .item .statusIcon {
    background: #282828;
    width: 46px;
    min-width: 46px;
    height: 46px;
    text-align: center;
    line-height: 44px;
    border-radius: 0;
    float: left;
    margin-right: 15px;
}
.sidebarUserAvatar {
    width: 210px;
    border-radius: 0;
    margin: -4px;
}
.pollResult .barContainer {
    height: 24px;
    line-height: 24px;
    display: inline-block;
    zoom: 1;
    vertical-align: middle;
    width: 100%;
    box-sizing: border-box;
    background: rgb(45, 45, 45);
        background-color: rgb(45, 45, 45);
        background-position-x: 0%;
        background-position-y: 0%;
        background-repeat: repeat;
        background-attachment: scroll;
        background-image: none;
        background-size: auto;
        background-origin: padding-box;
        background-clip: border-box;
    position: relative;
    border-radius: 0;
    overflow: hidden;
}
.message .item .userTagIcon {
    background-image: url(https://i.imgur.com/KFdqed2.png);
    content: 'ник в ответ';
}
}
