css:
/*
 * Craigslist - EasyOnTheEyesCraigslist
 *
 * Author      : DarkWatcher
 * Updated     : October 24, 2009
 * Description : Modifies the colors on Craigslist (http://www.craigslist.org/)
 * Usage       : Use with the Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 * Version     : 1.2
 */

@charset "UTF-8";
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document 
domain("craigslist.org"), 
domain("craigslist.at"), 
domain("craigslist.ca"), 
domain("craigslist.ch"), 
domain("craigslist.cz"), 
domain("craigslist.es"), 
domain("craigslist.fi"), 
domain("craigslist.gr"), 
domain("craigslist.it"), 
domain("craigslist.jp"), 
domain("craigslist.pl"), 
domain("craigslist.pt"), 
domain("craigslist.se"), 
domain("craigslist.co.in"), 
domain("craigslist.co.kr"), 
domain("craigslist.co.th"), 
domain("craigslist.co.uk"), 
domain("craigslist.co.za"), 
domain("craigslist.com.au"), 
domain("craigslist.com.cn"), 
domain("craigslist.com.mx"), 
domain("craigslist.com.ph"), 
domain("craigslist.com.sg"), 
domain("craigslist.com.tr"), 
domain("craigslist.com.tw")

{
/* Begin Styles */
body, body.hp, #leftbar, #logo {
	background: #26303c url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAE/CAYAAACHEqngAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAABUSURBVHjaYmJgYPjPBCQYmAiyiFBCBIsSxWSqw8YlUwlOMWpLUFsJmYppbMrAE2B//CPswX94ff5v2AUJfU2hcTagjgR1ShBqF25ULG1JK/kBAgwAT3T9gBTuL/MAAAAASUVORK5CYII=") repeat-x !important;
	color: #F8F8FF !important;
}
div.bchead {
	background: transparent !important;
}
blockquote form table {
	background: transparent !important;
	border-top: 1px solid #333  !important;
        border-right: 3px solid #000 !important;
        border-bottom: 3px solid #000 !important;
        border-left: 1px solid #333 !important;
	padding: 4px !important;
}
#search div {
	background: transparent !important;
	font-weight: bold !important;
}
li {
	background: transparent !important;
	margin: 3px 0 !important;
        font-size: 11px !important;
}
a {
	color: #F8F8FF !important;
}
a:visited, a:hover {
	color: #F8F8FF !important;
}
p {
        display:block !important;
        width:100% !important;
        line-height: 30px !important;
        margin: 1px !important;
        padding-left: 6px !important;
}
p:hover {
        background: #293B43 !important;
        width:99% !important;
        overflow:hidden !important;
        border: 1px solid #000 !important;
        border-width: 0 2px 0 2px !important;
        padding-left: 4px !important; 
}
.ban {
	border-bottom: 1px solid #F8F8FF !important;
}
.ban, table tr th, table tr td.tdy, table tr td {
	background: transparent !important;
}
#topban div {
	background: transparent !important;
	font-variant: small-caps !important;
	font-size: 20px !important;
}
div h4, div h4 a, h5, blockquote h4, .sh {
	background: transparent !important;
        font-variant: small-caps !important;
	font-size: 13px !important;
	font-weight: normal !important;
	color: #FFCC00 !important;
        text-align: left !important;
}
div h4, blockquote h4, h5 {
        border-top: 1px solid #333  !important;
        border-right: 3px solid #000 !important;
        border-bottom: 3px solid #000 !important;
        border-left: 1px solid #333 !important;
}
#flags {
	background: transparent !important;
        border-top: 1px solid #333  !important;
        border-right: 3px solid #000 !important;
        border-bottom: 3px solid #000 !important;
        border-left: 1px solid #333 !important;
}
#tsb {
        background: transparent !important;
        width: 86% !important;
        border-top: 1px solid #333  !important;
        border-right: 3px solid #000 !important;
        border-bottom: 3px solid #000 !important;
        border-left: 1px solid #333 !important;
        
}
#tsb a {
	color: red !important;
}
.blurbs li {
	width: 40% !important;
}
table tr td {
	vertical-align: top;
}
span {
	background: transparent !important;
}
#postlks, #leftlinks {
	text-align: justify !important;
}

/* Begin Main Craigslist Styles */

.www font {
	color:#F8F8FF !important;
        font-variant: small-caps !important;
}
.www td {
        border-bottom: 1px solid #F8F8FF !important;
}
.www td a {
	background: transparent !important;
        border-bottom: 1px solid white !important;
}
.www b {
	background: transparent !important;
        font-variant: small-caps !important;
        color: #FFCC00 !important;
}
.wwl td a {
	background:#F4F4F4;
	text-align:justify !important;
        font-variant: small-caps !important;
        font-size: 14px !important;
}
center {
        font-variant: small-caps !important;
        text-align:justify !important;
}
#query, .min, .max, #cAbb {
        background-color: #CCCCCC !important;
}
/* End Main Craigslist Styles */

/* Begin Login*/
blockquote table {
        background: transparent !important;
        border: 1px solid #F8F8FF !important;
}
blockquote table tr td {
        border-bottom: none !important;
}
/* End Login */

/* Logged in settings*/
body table tr td table {
        background: transparent !important;
}
blockquote table {
        border:none !important;
}
blockquote table tr td fieldset {
        background: transparent !important;
        border: 1px outset #F8F8FF !important;
}
blockquote table tr td fieldset legend {
        border: 1px solid #F8F8FF !important;
        border-width: 0 1px !important;
        font-variant:small-caps !important;
        padding:3px 5px 5px !important;
        font-size:14px !important;
        font-weight: normal !important;
}
blockquote table tr td fieldset form table {
        border: none !important;
}
blockquote table tr {
        background: transparent !important;
}
/* End Logged in settings*/

/* Postings */
body table {
        background: transparent !important;
}
/* End Postings */

/* End Logged in settings*/
form fieldset {
        background: transparent !important;
}
form fieldset legend {
        border: 1px solid #F8F8FF !important;
        border-width: 0 1px !important;
        font-variant:small-caps !important;
        padding:3px 5px 5px !important;
        font-size:14px !important;
        font-weight: normal !important;
}
form[id="postingForm"] {
        background: transparent !important;
}
div[class="posting sys"] {
        background: transparent !important;
}
.req {
        color:#F8F8FF !important;
}
textarea[class="req"], input[class="req"] {
        color: #000 !important;
}
div[class="highlight"] {
        background: transparent !important;
}
div[class="posting"] {
        background: transparent !important;
}
tr[bgcolor="#eeeeee"] {
        background: transparent !important;
}
/* End Styles */
}
