css:
@-moz-document domain("docs.google.com"), domain("sharepoint.com"), domain("microsoft.com"), domain("live.com"), domain("office.com"), domain("office365.com") {
.overlay-container-ltr div, .overlay-container-rtl div, .uberlay, .selection, .ewr-cell-selection-highlight-all, .ewr-cell-presence-highlight-all {

    --animate: 0.1s;
    transition: top var(--animate), left var(--animate), bottom var(--animate), right var(--animate), width var(--animate), height var(--animate);

}


.overlay-container-ltr div, .overlay-container-rtl div, .uberlay, .selection {

    --animate: 0.1s;
    transition: top var(--animate), left var(--animate), bottom var(--animate), right var(--animate), width var(--animate), height var(--animate);

}

.selection-border, .range-border, .active-cell-border, .input-box, .ewr-cell-selection-highlight-all, .ewr-selection-filldragborder {
    border-color: /*[[selection]]*/;
}

.selection, .autofill-handle, .ewr-selection-fillhandle {
    background-color: /*[[selection]]*/ !important;
}

}
