/* ==UserStyle==
@name           Lishogi - Lichess Horsey instead of knight
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Replace the knight with Lichess' horsey | Horsey made by Cham and SP | Lishogi.org
@author         Yohaan Nathan (TheYoBots)
==/UserStyle== */

@-moz-document domain("lishogi.org")
{
    piece.knight.sente,
      .cg-wrap.orientation-sente piece.knight.sente,
      .cg-wrap.orientation-gote piece.knight.sente,
      .spare-top piece.knight.sente,
      .cg-wrap.orientation-gote + #promotion-choice.bottom  piece.knight,
      .cg-wrap.orientation-sente + #promotion-choice.top piece.knight
  {
    background-image: url("https://github.com/ornicar/lila/raw/master/public/piece/horsey/bN.svg")!important;
  }

    piece.knight.gote,
      .cg-wrap.orientation-sente piece.knight.gote,
      .cg-wrap.orientation-gote piece.knight.gote,
      .pocket-bottom piece.knight.gote,
      .e-pocket-bottom piece.knight.gote,
      .spare-bottom piece.knight.gote,
      .cg-wrap.orientation-sente + #promotion-choice.bottom  piece.knight,
      .cg-wrap.orientation-gote + #promotion-choice.top  piece.knight
  {
    background-image: url("https://github.com/ornicar/lila/raw/master/public/piece/horsey/wN.svg")!important;
  }
}
