css:
@-moz-document domain("vk.com") {
[dir] body.new_header_design #page_header_cont {
    background-color: #19003c;
    border-bottom: 1px solid #19003c;
}
.TopHomeLink {
    color: #ffffff;
}
.top_audio_player_btn svg {
    vertical-align: middle;
    color: #ffffff;
}
.top_audio_player .top_audio_player_title {
    display: inline-block;
    overflow: hidden;
    opacity: 1;
    color: #ffffff;
    transition: opacity 190ms ease, top 300ms ease;
    transition-property: opacity, top;
}
.top_notify_btn .TopNavBtn .TopNavBtn__notify {
    margin-top: 7px;
}
.TopNavBtn .TopNavBtn__icon {
    color: #ffffff;
    opacity: 1;
    margin-top: 5.5px;
}
