css:
@-moz-document url-prefix("https://www.youtube.com/") {
ytd-thumbnail #thumbnail.ytd-thumbnail 
{
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    border-radius: 10px;
}

ytd-thumbnail #thumbnail.ytd-thumbnail:hover
{
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    border-radius: 10px;
    border: solid 4px;
}

#avatar.ytd-rich-grid-media
{
    background-color: var(--yt-spec-10-percent-layer)!important;
    width: 36px;
    height: 36px;
    border-radius: 5px;
    background-color: transparent;
    overflow: hidden;
}

ytd-video-meta-block[rich-meta][mini-mode] #metadata-line.ytd-video-meta-block 
{
    font-family: "Roboto","Arial",sans-serif;
    font-size: 1.2rem;
    line-height: 1.8rem;
    font-weight: 400;
    max-height: 3.6rem;
    overflow: hidden;
    display: block;
    -webkit-line-clamp: 2;
    display: box;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    text-overflow: ellipsis;
    white-space: normal;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    background-color: #ffffffc7;
    color: #838383;
    border-radius: 6px;
    justify-content: center;
}

#button.ytd-menu-renderer yt-icon.ytd-menu-renderer 
{
    color: var(--ytd-menu-renderer-button-color,var(--yt-spec-icon-inactive));
    background-color: #ffffff36;
    border-radius: 3px;
}

#metadata-line.ytd-video-meta-block span.ytd-video-meta-block 
{
    display: inline-block;
    color: black;
    font-weight: 800;
}

yt-chip-cloud-chip-renderer
{
    height: 32px;
    min-width: 12px;
    padding: 0 10px;
    border-radius: 4px;
    box-sizing: border-box;
    outline: none;
    overflow: hidden;
    cursor: pointer;
    user-select: none;
    position: relative;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
    display: -ms-inline-flexbox;
    display: -webkit-inline-flex;
    display: inline-flex;
    font-size: var(--ytd-user-comment_-_font-size);
    font-weight: var(--ytd-user-comment_-_font-weight);
    line-height: var(--ytd-user-comment_-_line-height);
    letter-spacing: var(--ytd-user-comment_-_letter-spacing);
    font-family: var(--ytd-user-comment_-_font-family);
    color: var(--ytd-user-comment_-_color);
}

yt-img-shadow.ytd-topbar-menu-button-renderer
{
    width: 32px;
    height: 32px;
    cursor: pointer;
    margin: 0 8px;
    border-radius: 4px;
    background-color: transparent;
    overflow: hidden;
}

#voice-search-button.ytd-masthead .ytd-masthead[is-icon-button], #buttons.ytd-masthead .ytd-masthead[is-icon-button] 
{
    color: var(--yt-spec-icon-active-other);
    outline: none;
    background-color: #717171;
    border-radius: 4px;
}

.title.ytd-guide-entry-renderer 
{
    color: var(--yt-spec-text-primary);
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    font-family: "Roboto","Arial",sans-serif;
    font-size: 1.4rem;
    line-height: 2rem;
    font-weight: 400;
    -ms-flex: 1 1 0.000000001px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: 0.000000001px;
    flex-basis: 0.000000001px;
    background-color: gray;
    border-radius: 10px;
    text-align: center;
}

yt-img-shadow.ytd-guide-entry-renderer {
    height: 24px;
    width: 24px;
    margin-right: 24px;
    border-radius: 4px;
    background-color: transparent;
    overflow: hidden;
}

ytd-grid-video-renderer:not([rich-meta]) #metadata-line.ytd-grid-video-renderer {
    font-family: "Roboto","Arial",sans-serif;
    font-size: 1.2rem;
    line-height: 1.8rem;
    font-weight: 400;
    max-height: 3.6rem;
    overflow: hidden;
    display: block;
    -webkit-line-clamp: 2;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    background-color: #ffffffc7;
    color: #838383;
    border-radius: 2px;
}

ytd-grid-video-renderer:not([rich-meta]) #metadata-line.ytd-grid-video-renderer {
    font-family: "Roboto","Arial",sans-serif;
    font-size: 1.2rem;
    line-height: 1.8rem;
    font-weight: 400;
    max-height: 3.6rem;
    overflow: hidden;
    display: block;
    -webkit-line-clamp: 2;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    background-color: #ffffffc7;
    /* color: #838383; */
    border-radius: 2px;
    /* text-align: center; */
    color: black;
    font-weight: 800;
}

#info.ytd-channel-renderer {
    padding: 0 16px 16px 16px;
    min-width: 0;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: column;
    -webkit-flex-direction: column;
    flex-direction: column;
    -ms-flex-pack: center;
    -webkit-justify-content: center;
    justify-content: center;
    -ms-flex: 1 1 0.000000001px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: 0.000000001px;
    flex-basis: 0.000000001px;
    background-color: gray;
    border-radius: 5px;
}

yt-img-shadow.ytd-channel-renderer {
    border-radius: 5px;
    background-color: transparent;
    overflow: hidden;
    height: 136px;
    width: 136px;
    margin-right: 16px;
}

.tab-content.tp-yt-paper-tab {
    height: auto;
    transform: translateZ(0);
    -webkit-transform: translateZ(0);
    transition: opacity 0.1s cubic-bezier(0.4, 0, 1, 1);
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
    -ms-flex-pack: center;
    -webkit-justify-content: center;
    justify-content: center;
    -ms-flex: 1 1 auto;
    -webkit-flex: 1 1 auto;
    flex: 1 1 auto;
    background-color: gray;
    border-radius: 2px;
    font-size: 15px;
    padding: 0 15px;
}

#avatar.ytd-c4-tabbed-header-renderer {
    width: 80px;
    height: 80px;
    margin: 0 24px 0 0;
    -ms-flex: none;
    -webkit-flex: none;
    flex: none;
    border-radius: 15px;
    background-color: transparent;
    overflow: hidden;
}

#subscriber-count.ytd-c4-tabbed-header-renderer {
    color: var(--yt-lightsource-secondary-title-color);
    font-family: "Roboto","Arial",sans-serif;
    font-size: 1.6rem;
    line-height: 2rem;
    font-weight: 800;
    background-color: gray;
    border-radius: 2px;
    height: fit-content;
    padding: 0 15px;
}
}
