@-moz-document url-prefix("https://app.proficonf.com") {

/* Импорт выбора пользователя */

/*[[resizable]]*/
/*[[bg-effects]]*/
/*[[av-bg]]*/

/* Задний фон */
#conference user-video {
	background: center / 100% no-repeat url(/*[[image]]*/);
}
.fullsize{
	background: transparent;
}

/* Шапка и боковое меню */
.conference-topbar {
	background: /*[[t-color]]*/ !important;
}
#conference-sidebar {
	background: /*[[s-color]]*/ !important;
}





/* Шапка */
#conference-topbar {
	transition-duration: 0.2s;
	box-shadow: 0 10px 20px 0 rgba(0, 0, 0, 0.4);
}
#conference-topbar:hover {
	box-shadow: 0 10px 20px 5px rgba(0, 0, 0, 0.4);
}
.conference-title__preview {
	transition: 0.1s;
	padding: 5px 15px 5px 15px;
	border-radius: 15px !important;
}
.conference-topbar-mc-item > .conference-topbar-mc-item__button {
	box-shadow: none !important;
	background: rgb(0 0 0 / 25%) !important;
	transition: 0.2s !important;
	border-radius: 50% !important;
	border: none !important;
}
.conference-topbar-mc-item > .conference-topbar-mc-item__button:hover {
	box-shadow: 0 0 20px 5px rgba(255, 255, 255, 0.4) !important;
	border-radius: 25% !important;
}





/* Боковое меню */
#conference-sidebar {
	transition-duration: 0.15s;
	box-shadow: 0 0 30px 0 rgba(0, 0, 0, 0.4) !important;
}
#conference-sidebar:hover {
	width: 70px !important;
	box-shadow: 0 0 30px 10px rgba(0, 0, 0, 0.4) !important;
}
.sidebar.opened {
	transition-duration: 0.15s !important;
}
/* Размер чата в соответствии с размером бокового меню */
#conference-sidebar:hover ~ .ng-isolate-scope > .sidebar.opened {
	transform: translate(20px);
}

/* Кнопки в боковом меню */
.sidebar-buttons__item {
	transition-duration: 0.2s;
}
.sidebar-buttons__item:hover {
	background: rgb(0 0 0 / 25%);
	transform: scale(0.75);
	border-radius: 20px;
}
.sidebar-buttons__button {
	background: transparent !important;
}

/* Загрузка бокового меню */
.sidebar-skeleton:after {
	opacity: 0.2;
}





/* Меню участников */
.participants-list__inner--bg {
	background: none !important;
}
.sidebar.opened {
	background: /*[[cp-bg-color]]*/;
}
.sidebar-header {
	background: /*[[cp-bg-color]]*/;
	border-bottom: 2px solid rgb(0 0 0 / 25%);
	color: /*[[cp-f-color]]*/;
}
.sidebar-header__title {
	background-: /*[[cp-bg-color]]*/;
	color: /*[[cp-f-color]]*/;
}
/* Анимации */
participants {
	transition-duration: 0.15s !important;
	box-shadow: 0 0 30px 0 rgba(0, 0, 0, 0.4) !important;
}
participants:hover {
	box-shadow: 0 0 30px 10px rgba(0, 0, 0, 0.4) !important;
}
/* Остальное */
.participants-list__title {
	background: /*[[cp-bg-color]]*/;
	border: none;
	color: /*[[cp-f-color]]*/;
}
.participant {
	background: /*[[cp-bg-color]]*/;
	border: none;
}
.participant-name {
	color: /*[[cp-f-color]]*/;
}
.status-text {
	color: /*[[cp-s-color]]*/;
	opacity: 0.75;
}





/* Чат */
/* Когда чат чист */
 .chat-content-placeholder {
	filter: invert(1);
	opacity: 0.25;
}
.chat-content-placeholder span {
	filter: invert(1);
	color: /*[[cp-s-color]]*/;
}
/* Анимации */
conference-chat {
	transition-duration: 0.15s !important;
	box-shadow: 0 0 30px 0 rgba(0, 0, 0, 0.4) !important;
}
conference-chat:hover {
	box-shadow: 0 0 30px 10px rgba(0, 0, 0, 0.4) !important;
}
/* Сообщения в чате */
.conference-chat__main {
	background: /*[[cp-bg-color]]*/;
}
.chat-message__text {
	color: /*[[cp-s-color]]*/;
}
 .chat-message_active:hover {
	transition-duration: 0.2s;
	background: rgb(0 0 0 / 25%);
}

/* Редактирование сообщения */
.item-menu-button {
	transition-duration: 0.2s;
	border-radius: 15px;
}
.conference-chat-footer__textarea {
	transition-duration: 0.15s;
	border-radius: 15px;
}
.conference-chat-footer__textarea:focus {
	box-shadow: 0 0 10px 0 rgba(255, 255, 255, 0.4);
}

/* Три точки */
 .item-menu-button:hover {
	background: rgb(0 0 0 / 25%);
}
.conference-chat-footer {
	border-top: 2px solid rgb(0 0 0 / 25%);
	background-color: /*[[cp-bg-color]]*/;
}
.conference-chat-footer__textarea {
	border: none;
	color: #fff;
	background-color: rgb(0 0 0 / 25%);
}





/*Меню эмодзи*/
 .emojis-dropdown__menu {
	border: none !important;
	border-radius: 15px !important;
	backdrop-filter: blur(3px) !important;
	background: rgb(0 0 0 / 50%) !important;
	box-shadow: 0 10px 20px 10px rgba(0, 0, 0, 0.4) !important;
}





/*Подсказки*/
 .tooltip-inner {
	color: #fff !important;
	border: none !important;
	border-radius: 15px !important;
	backdrop-filter: blur(3px) !important;
	background: rgb(0 0 0 / 50%) !important;
	box-shadow: 0px 10px 30px 10px rgba(0, 0, 0, 0.4) !important;
}
.tooltip.right .tooltip-arrow {
	border-right-color: rgb(0 0 0 / 50%) !important;
}
.tooltip.bottom .tooltip-arrow {
	border-bottom-color: rgb(0 0 0 / 50%) !important;
}
.item-menu-links {
	box-shadow: 0px 10px 20px 10px rgba(0, 0, 0, 0.4) !important;
	background: rgb(0 0 0 / 50%);
	backdrop-filter: blur(5px);
	border-radius: 15px;
	border: none;
}
.item-menu-links__list-link {
	color: #fff;
	background: transparent !important;
}





/* Кнопка закрытия */
.pf-close-symbol{
	transition-duration: 0.2s;
}
.pf-close-symbol:hover{
	transform: rotate(25deg);
}
.pf-close-symbol:not(:active) {
	animation-duration: 0.5s;
	animation-name: Close;
}
@keyframes Close {
 from {
 transform: rotate(25deg);
 }

 to {
 transform: rotate(360deg);
 }
}





/* Плавная загрузка */
.opaque {
	margin-top: 0;
	transition: 0.5s;
	animation: 0.5s OpaqueMargin;
}
@keyframes OpaqueMargin {
 from {
 margin-top: 50px;
 }

 to {
 margin-top: 0;
}
}
}

