css:
/* User-Style for Fresh.co.il */
/*
	Name: fresh - element skin ~fat~
	Author: Baruch Mustakis a.k.a ShoobyD.
	Revised: 25/10/09 (in dd/mm/yy format)
	site: http://u.math.biu.ac.il/~mustakb/
*/


/* rules */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("fresh.co.il") {


body {
	background: #B7BCE2 !important;
	color: #4D528C !important;
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	margin: 0px 0px 0px 0px !important;
	padding: 0px !important;
}

a:link {color: #4D528C !important; text-decoration: none !important;}
a:visited {color: #4D528C !important; text-decoration: none !important;}
a:hover, a:active {color: #4D528C !important; text-decoration: underline !important;}

.page {background: #FFFFFF !important; color: #4D528A !important;}

td, th, p, li {font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;}


/********************table styles********************/

.tborder {
	background: #e2e1ea !important;
	color: #000000 !important;
	border: 1px solid #7b81cd !important;
}

.tcat {
	background: #4F57A0 url('data:image/gif;base64,') repeat-x top left !important;
	color: #E0E0F6 !important;
	font: bold 10pt tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.tcat a:link {color: #E0E0F6 !important; text-decoration: none !important;}
.tcat a:visited {color: #E0E0F6 !important; text-decoration: none !important;}
.tcat a:hover, .tcat a:active {color: #E0E0F6 !important; text-decoration: underline !important;}

.thead {
	background: #d4d7f3 url('data:image/gif;base64,R0lGODlhAgAXAMQAALe84ra87ra847m/7rC36bC369TX87C36LO55cfM8bS65LW747G47LO67bC36rG35s7S8sTJ8NHV8rzC77C37MDF8MvP8QAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAAAAAAALAAAAAACABcAAAUmIAAIwrIoCoI8z3EQhOMUBUUxTNMEwTBMk0olEkkkLBYIRCIxGEIAOw==') repeat-x top left !important;
	color: #FFFFFF !important;
	font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.thead a:link {color: #FFFFFF !important; text-decoration: none !important;}
.thead a:visited {color: #FFFFFF !important; text-decoration: none !important;}
.thead a:hover, .thead a:active {color: #FFFFFF !important; text-decoration: underline !important;}

.tfoot {
	background: #4F57A0 url('data:image/gif;base64,') repeat-x top left !important;
	color: #E0E0F6 !important;
}
.tfoot a:link {color: #E0E0F6 !important; text-decoration: none !important;}
.tfoot a:visited {color: #E0E0F6 !important; text-decoration: none !important;}
.tfoot a:hover, .tfoot a:active {color: #E0E0F6 !important; text-decoration: underline !important;}

/********************end table styles********************/


/********************alts styles********************/
.alt1, .alt1Active {
	background: #E4E6F5 !important;
	color: #4D528C !important;
	border-left: 1px solid #FFFFFF !important;
	border-top: 1px solid #FFFFFF !important;
}
.alt1 a:link, .alt1Active a:link {color: #4D528C !important; text-decoration: none !important;}
.alt1 a:visited, .alt1Active a:visited {color: #4D528C !important; text-decoration: none !important;}
.alt1 a:hover, .alt1 a:active, .alt1Active a:hover, .alt1Active a:active {color: #4D528C !important; text-decoration: underline !important;}

.alt2, .alt2Active {
	background: #F5F5FF !important;
	color: #4D528C !important;
	border-left: 1px solid #FFFFFF !important;
	border-top: 1px solid #FFFFFF !important;
}
.alt2 a:link, .alt2Active a:link {color: #4D528C !important; text-decoration: none !important;}
.alt2 a:visited, .alt2Active a:visited {color: #4D528C !important; text-decoration: none !important;}
.alt2 a:hover, .alt2 a:active, .alt2Active a:hover, .alt2Active a:active {color: #4D528C !important; text-decoration: underline !important;}

/********************end alts styles********************/


td.inlinemod a:link {color: #4D528C !important; text-decoration: none !important;}
td.inlinemod a:visited {color: #4D528C !important; text-decoration: none !important;}
td.inlinemod a:hover, td.inlinemod a:active {color: #4D528C !important; text-decoration: underline !important;}

.wysiwyg {
	background: #F5F5FF !important;
	color: #000000 !important;
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}

textarea, .bginput {font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;}
.button {font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;}
select {font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;}
option, optgroup {font-size: 11px !important; font-family: verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;}
.smallfont {font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;}
.time {color: #666686 !important;}
.navbar {font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;}
.highlight {color: #FF0000 !important; font-weight: bold !important;}
.fjsel {background: #3E5C92 !important; color: #E0E0F6 !important;}
.fjdpth0 {background: #F7F7F7 !important; color: #000000 !important;}

.panel {
	background: #E4E7F5 url('http://www.fresh.co.il/vBulletin/images/gradients/gradient_panel.gif') repeat-x top left !important;
	color: #000000 !important;
	padding: 10px !important;
	border: 2px outset !important;
}
.panelsurround {
	background: #D1D4E0 url('http://www.fresh.co.il/vBulletin/images/gradients/gradient_panelsurround.gif') repeat-x top left !important;
	color: #000000 !important;
}

legend {color: #22229C !important; font: 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;}



/********************vbmenu styles********************/

.vbmenu_control {
	background: #4F57A0 url('data:image/gif;base64,') repeat-x top left !important;
	color: #E0E0F6 !important;
	font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	padding: 3px 6px 3px 6px !important;
	white-space: nowrap !important;
}
.vbmenu_control a:link {color: #E0E0F6 !important; text-decoration: none !important;}
.vbmenu_control a:visited {color: #E0E0F6 !important; text-decoration: none !important;}
.vbmenu_control a:hover, .vbmenu_control a:active {color: #E0E0F6 !important; text-decoration: underline !important;}

.vbmenu_popup {
	background: #D1D3E2 !important;
	color: #000000 !important;
	border: 1px solid #7b81cd !important;
}

.vbmenu_option {
	background: #F5F5FF !important;
	color: #4D528C !important;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	white-space: nowrap !important;
	cursor: pointer !important;
	border-left: 1px solid #FFFFFF !important;
	border-top: 1px solid #FFFFFF !important;
}
.vbmenu_option a:link {color: #4D528C !important; text-decoration: none !important;}
.vbmenu_option a:visited {color: #4D528C !important; text-decoration: none !important;}
.vbmenu_option a:hover, .vbmenu_option a:active {color: #4D528C !important; text-decoration: none !important;}

.vbmenu_hilite {
	background: #E4E6F5 !important;
	color: #4D528C !important;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	white-space: nowrap !important;
	cursor: pointer !important;
	border-left: 1px solid #FFFFFF !important;
	border-top: 1px solid #FFFFFF !important;
}
.vbmenu_hilite a:link {color: #4D528C !important; text-decoration: none !important;}
.vbmenu_hilite a:visited {color: #4D528C !important; text-decoration: none !important;}
.vbmenu_hilite a:hover, .vbmenu_hilite a:active {color: #4D528C !important; text-decoration: none !important;}

/********************end vbmenu styles********************/


.bigusername { font-size: 14pt !important; }
td.thead, div.thead { padding: 4px !important; }
.pagenav a { text-decoration: none !important; }
.pagenav td { padding: 2px 4px 2px 4px !important; }
.fieldset { margin-bottom: 6px !important; }
.fieldset, .fieldset td, .fieldset p, .fieldset li { font-size: 11px !important; }
form { display: inline !important; }
label { cursor: default !important; }
.normal { font-weight: normal !important; }
.inlineimg { vertical-align: middle !important; }


/********************element_postbit style********************/
/* .element_postbit class => #posts .alt1 hr + div rule */
#posts .alt1 hr + div {
	color: #4D528C !important;
	font: 12px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	text-decoration: none !important;
}
#posts .alt1 hr + div a:link {
	color: #4D528C !important;
	font: 12px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	border-bottom: 1px dotted #FF0000 !important;
	text-decoration: none !important;
}
#posts .alt1 hr + div a:visited {
	color: #4D528C !important;
	font: 12px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	border-bottom: 1px dotted #FF0000 !important;
	text-decoration: none !important;
}
#posts .alt1 hr + div a:hover, .aria_postbit a:active {
	color: #4D528C !important;
	font: 12px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	border-bottom: 1px dotted #4D528C !important;
	text-decoration: none !important;
}
/********************end element_postbit style********************/


/********************element_control_nav style********************/
/* .element_control_nav class => tr[align="center"] .vbmenu_control rule */
tr[align="center"] .vbmenu_control {
	color: #FFFFFF !important;
	font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	padding: 3px 6px 3px 6px !important;
	white-space: nowrap !important;
}
tr[align="center"] .vbmenu_control a:link {color: #FFFFFF !important; text-decoration: none !important;}
tr[align="center"] .vbmenu_control a:visited {color: #FFFFFF !important;text-decoration: none !important;}
tr[align="center"] .vbmenu_control a:hover, .vbmenu_control_nav a:active {color: #FFFFFF !important; text-decoration: underline !important;}
/********************end element_control_nav style********************/

hr {
	border: 0 !important;
	height: 1px !important;
	background: #fff url('data:image/gif;base64,R0lGODlhAwABAIAAAOTm9be84iH5BAAAAAAALAAAAAADAAEAAAICTFAAOw==') repeat scroll center !important;
}


/* banner fix */
.BannerBack {background: none !important;}
body > table:first-of-type {
	height: 114px !important;
	background: #9296d3 url('data:image/gif;base64,')  !important;
}
body > table:first-of-type > tbody > tr {height: 1px !important;}
body > table:first-of-type > tbody > tr:nth-child(2) {display: none !important;}
body > table:first-of-type > tbody > tr:nth-child(1) {height: 91px !important;}
body > table:first-of-type > tbody > tr:nth-child(3) {height: 23px !important;}
body > table:first-of-type > tbody > tr:nth-child(3) table tr {height: 18px !important;}
body > table:first-of-type > tbody > tr:nth-child(3) table img {height: 7px !important;}


/* fades fix */
.Fade1 {background-color: #a1a1d8 !important;}
.Fade2 {background-color: #b4b3dd !important;}
.Fade3 {background-color: #cfcee5 !important;}
.Fade4 {background-color: #edebed !important;}


/********************in-page styles fix********************/
.lobby + div[align="center"] .tborder {height: 30px !important;}
.page > div > .tborder > tbody > tr > td {padding: 6px !important;}
.page > div > .tborder > tbody > tr > td:only-child {padding: 0px !important;}
.tcat {padding: 6px !important;}
/*
#poststop ~ .tborder td {padding: 6px !important;}
[name="vbform"] .tcat {padding: 6px !important;}
*/
/********************end in-page styles fix********************/


}
