css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("craigslist.ca"),domain("craigslist.org") {
.city,#forums,#leftlinks,#topban a,.ban,#logo sup,#footer,#tsb,.clfooter,.blurbs,sup,#ef,#flagMsg,#flags br{display:none}

#postlks li{display:none}
#postlks li:first-child{display:block!important}

.hp{margin:0}
#topban{position:absolute;left:0;top:360px;font-size:12px;width:200px!important;display:inline!important}
#logo{background:none!important;text-shadow:0 1px 1px #000;font-size:36px!important;}
#logo a{color:#57e!important}

h1,h2,h3{font-family:Georgia!important;}
body{font-family:helvetica,verdana,sans-serif!important}

table{width:100%!important}
.cats ul{width:99.4%!important;text-indent:10px}
#res h4{-moz-border-radius:6px!important}

.bchead{position:absolute;top:0;left:0;right:0}

.posting,.toc{padding-top:50px}
#flags{margin:0!important;position:absolute;right:0;top:-1px;padding:4px!important}
#flags a.fl{display:inline!important}

h4{-moz-border-radius:6px 6px 0px 0px!important;-moz-box-shadow:inset 0 0 8px #eee}

/*MOAR*/
#alks{display:none}
td:last-child{display:none}
#leftbar td:last-child{display:block}
#leftbar {width:200px}
.cats li{-moz-box-shadow:inset 0 0 3px #aaa}
blockquote form table tr{display:inline!important;}
blockquote form table tbody tr td{text-align:left!important;width:auto!important}
}
