@-moz-document domain("ffonts.net") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.fontcss_category,
.category_title_sidebar
{
	text-transform: capitalize!important;
}

span.glyphs_spec,
:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.fpreview1 {
    overflow-y: visible;
}

/* Fonts 
_____________________________________________________________________________________________________*/

span.glyphs_spec
{
	font-size: 31px!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.tac,
.ut-t.w-5.sidebarcss_ffont,

[id*='div-gpt-ad-'],
[id*='ffonts.net_'],
[id*='google_ads_iframe'],
[id*='pa-container-'],
[id*='stpd_ffonts.net_']
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.cnt {
    left: 0!important;
}

#content {
    margin-left: 45px!important;
}

.fontlist>li {
	margin-bottom: 4rem!important;
}

label.labelpreviewform {
    top: 7px;
    left: 20px;
}

/* Margins 
_____________________________________________________________________________________________________*/

#sidebar
{
	margin: 1.2rem!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

#sidebar,
	
.fontlist,
.fontlist>li
{
	padding: 1.2rem!important;
}

.page-item.active.s24
{
	padding: 8px 16px!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

.fpreview1
{
	height: auto!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.cnt
{
	max-width: none!important;
}

.cnt
{
	width: 98%!important;
}

.w-11 {
    width: 70.75%;
}

.w-5 {
    width: 20.25%;
}

}

@-moz-document domain("ffonts.net") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.page-item.active.s24,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.blk,
	
body,
li,
li.page-item a,
p,
span.glyphs_spec
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.infofont_css,
.subtitle_ffontcss,
.titleglyphs,

a.freefontsbreadcrumblink,
h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

.fontcss_category,

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

.authorbyfreefonts,

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.authorbyfreefonts strong,

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.freetexcss
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}


}

@-moz-document domain("ffonts.net") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.topbardesign,

footer
{
	background: #060505!important;
	background-color: #060505!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#content,
#main.wsb,

.page-link,

label.labelpreviewform,
ul.fontlist
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.butn.red[title*="Download"]
{	
	background: radial-gradient(circle, #0f79af 0%, #031017 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.butn.red[title*="Download"]::before
{
	background: radial-gradient(circle, #031017 0%, #0f79af 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.butn.red[title*="Download"]:hover::before
 {
  opacity: 1!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.applyfilter_button,
.commercialfontscss,
.signinbutton
{	
	background: radial-gradient(circle, #380002 0%, #870101 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.5rem!important;
	position: relative!important;
	z-index: 1!important;
}

.applyfilter_button::before,
.commercialfontscss::before,
.signinbutton::before
{
	background: radial-gradient(circle, #870101 0%, #380002 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.applyfilter_button:hover::before,
.commercialfontscss:hover::before,
.signinbutton:hover::before {
  opacity: 1!important;
}

/* Orange 
_____________________________________________________________________________________________________*/

.butn.gry
{	
	background: radial-gradient(circle, #651c03 0%, #e1410b 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.butn.gry::before
{
	background: radial-gradient(circle, #e1410b 0%, #651c03 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.butn.gry:hover::before {
  opacity: 1!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.butn.red
{	
	background: radial-gradient(circle, #066503 0%, #3de10b 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .25rem!important;
	position: relative!important;
	z-index: 1!important;
}

.butn.red::before
{
	background: radial-gradient(circle, #3de10b 0%, #066503 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.butn.red:hover::before {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

#main #sidebar,
	
.fontlist:not(ul)
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

#sticky-popup,

.cardthirdgridlayoutbox
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

#sidebar:hover,
#sticky-popup:hover,

.cardthirdgridlayoutbox:hover,
.fontlist:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.classicalfonts_sidebarcss li:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

.page-item.active.s24
{
	background: radial-gradient(ellipse at center, #07ed07 0%,#0000 70%)!important;
	padding: 13px 26px!important;
}

li.page-item a:hover
{
	background: radial-gradient(ellipse at center, #9a9d00 0%,#0e0e0e 70%)!important;
	padding: 13px 26px!important;
}


}

@-moz-document domain("ffonts.net") {
/* Image Settings */

.classicalfonts_sidebarcss li,
.fontlist>li
 {
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

.classicalfonts_sidebarcss li:hover,
.fontlist>li:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

@-moz-document domain("ffonts.net") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

/* Heart ________________________________________________________________________________*/

.classicalfonts_sidebarcss li a[title="Instagram Fonts"]::before {
	content: ' '!important;
}

/* On ___________________*/

.classicalfonts_sidebarcss li a[title="Instagram Fonts"]::before
{
	background: url(https://i.ibb.co/FB9kX4W/healthy-heart.png) no-repeat!important;
	box-sizing: border-box;
}

/* Off ___________________*/

selector
{
	background: url(https://i.ibb.co/cD452WP/healthy-hear-darkt-32x32.png) no-repeat!important;
	box-sizing: border-box;
}

.classicalfonts_sidebarcss li a[title="Instagram Fonts"]::before
{
    background-size: 25px!important;
    height: 26px!important;
    padding-left: 36px!important;
    width: 1px!important;
}

/* Search ___________________________________________________________________________*/

header .stacksearch .butn i
{
	background: url(https://i.ibb.co/cJFxJM6/search3.png) no-repeat!important;
	box-sizing: border-box;
}

header .stacksearch .butn i
{
	background-size: 15px!important;
	height: 20px!important;
	padding-left: 16px!important;
	width: 1px!important;
}

/* Shopping Cart ___________________________________________________________________*/

.ui-cart
{
	background: url(https://i.ibb.co/42GPC9s/shoppingcart.png) no-repeat!important;
	box-sizing: border-box;
}

.ui-cart
{
	background-size: 35px!important;
	height: 46px!important;
	padding-left: 36px!important;
	width: 1px!important;
}


}

@-moz-document domain("ffonts.net") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
