css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hungangels.com") {
table{
	max-width: 960px; 
}
font, th, td, p {
	font-family:Helvetica,Verdana,Arial,sans-serif !important;
}
th {
	color: #FFF !important;
	background-color: #8A0E19 !important;
}
td.row1, td.row2, td.row3{
	background-color: #FFCCFF !important;
}

td.cat, td.catHead, td.catSides, td.catLeft, td.catRight, td.catBottom {
background-color: #ffcff6 !important;
background-image:none !important;
border:none !important;
height:2em !important;
}

.cattitle {
text-align: center;
color: #0f0e1a !important;
font-size:1.5em  !important;
font-weight:bold !important;
letter-spacing:2px !important;
padding-left: 60px;
font-family: Georgia, "Times New Roman", Times, serif !important;
}
h2, a.maintitle, th.thHead, td.catHead {
font-family: Georgia, "Times New Roman", Times, serif !important;
}

th.thHead, td.catHead{
 background-color: #e5b5d1 !important;
 padding-left: 2em !important;
}
.topictitle{
margin-bottom: 5px !important;
}

a:link, a:visited, a.topictitle:link {
	color: #8A0E19 !important;
}
a:active, a.topictitle:active {
  color: #95454b !important;
  text-decoration: none !important;
}
a:hover, a:active, a.topictitle:hover, a.topictitle:active {
	text-decoration: underline !important;
}
.postdetails img{
max-height: 400px !important;
max-width: 200px !important;
}

input.liteoption, input.mainoption {
border: 1px solid #633a4a !important;
}

input.liteoption, input.mainoption, input.button, input.helpline  {
background-color: #f47b91 !important;
-moz-border-radius: 10px;
-webkit-border-radius: 10px;
}
 input.button, input.helpline  {	
 padding:  0 10px 0 5px !important;
 }
input.post, textarea.post, select , option{
background-color:#fff !important;
}

.genmed{
padding-left: 1em !important;
max-width: 600px !important;
display: block !important;
margin:  5px 0 !important;
}
.gensmall{
margin:  1px 0 !important;
padding-left: 0.5em !important;
max-width: 300px !important;
display: block !important
}
td.gensmall{
display:inline !important;
float: left !important;
}
.quote {
background-color:#fee !important;
border:2px solid #AE3FEF !important;
}

th {
	color: #fff !important;
	}
th.thHead, th.thSides, th.thTop, th.thLeft, th.thRight, th.thBottom, th.thCornerL, th.thCornerR {
border: none !important;
}

}
