css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
Name: github Grayrabbit
Author: Nikita Vasilyev
Description: http://github.com/nv/github-style/
*/

@-moz-document url-prefix(http://github.com/) {

body {
  background-color: #f6f6f6 !important;
}

#repo_menu ul, 
#repo_sub_menu,
#repo_menu ul li {
  background: none !important;
}

#repo_menu {
  border-bottom: 1px solid #E6E6E6 !important;
  overflow: visible !important;
}
#repo_menu ul {
  border-bottom: none !important;
  position: relative;
  top: 3px;
}
#repo_menu ul li {
  border-right: none !important;
}
#repo_menu ul li.active {
  -moz-border-radius-topleft: 1em;
  -moz-border-radius-topright: 1em;
  background: #f6f6f6 !important;
  border-top: 1px solid #D6D6D6;
}

#repo_sub_menu {
  border-top: 1px solid #CCC;
}
#repo_sub_menu li {
  font-size: .9em;
}
#repo_sub_menu a {
  margin: 0 1.5em !important;
  padding: 0 !important;
  color: #666 !important;
}
#repo_sub_menu a.active {
  border-bottom: none !important;
  color: #000 !important;
  background: none !important;
}


#main > .site {
  width: auto !important; 
  padding: 0 !important;
}
#main > .site > * {
  width: 69em !important; 
  margin-left: auto !important; 
  margin-right: auto !important;
}

#repos {}
#repos .public {
  background:none !important; 
  border: none !important;
}
#repo_details {
  width:69em; 
  margin-left: auto !important; 
  margin-right: auto !important;
}

#main > .site > #commit {
  width:auto !important; 
  background: #EAF2F5 !important; 
  border-bottom:1px solid #BEDCE7
}
#commit .envelope {
  width:69em; 
  margin-left: auto !important; 
  margin-right: auto !important; 
  border:none !important; 
  background: none !important;
}
#commit .message pre {  
  font-family: helvetica,arial,freesans,clean,sans-serif !important;
}

#path {
  font-size:100% !important; 
  padding: 1.4em 0 .2em !important;
}

#browser {
  margin-bottom: 4em !important;
}
#browser table {
  border: none !important;
}
#browser th {
  background: none !important; 
  border-bottom: 1px solid #DDD !important; 
  padding-bottom:0 !important; 
  color: #AAA !important;
}
#browser td {
  background: none !important; 
  border-bottom: 1px solid #EEE !important;
}
#browser tr:hover td {
  background: #EEE url(http://github.com/images/modules/browser/row_bg.png) repeat-x !important;
}

#browser th, 
#browser .age, 
#browser .message {
  font-family: helvetica,arial,freesans,clean,sans-serif;
}

#main > .site > #readme {
  width:auto !important; 
  border-top: 1px solid #DDD; 
  text-align: center;
}
#readme .name {
  position: relative; 
  top: -.7em; padding: 0 .6em !important; 
  text-align: center !important; 
  color:#BBB !important; 
  background: #f3f3f3; 
  font-size:100% !important;
}
#readme .wikistyle {
  width: 69em; 
  margin-left: auto !important; 
  margin-right: auto !important; 
  border:none !important; 
  background: none !important; 
  text-align: left;
}
.wikistyle h2 {
  border-top: none !important;
}
.wikistyle *:first-child {
  margin-top: 0 !important; 
  padding-top: 0 !important;
}
.wikistyle pre {
  border-width: 1px 0 !important;
}

.site > #dashboard {
  width:69em; 
  margin-left: auto !important; 
  margin-right: auto !important;
}
#dashboard .projects .project {
  background: none !important; 
  border: none !important;
}
#dashboard .projects .project + .project {
  margin-top: 3em !important;
}
#dashboard .projects .meta {
  font-size: 75% !important; 
  font-style: normal !important; 
  margin:0 !important;
}
#dashboard .projects .graph {
  margin-top: -2px !important;
}
.graph .bars {
  background:#F6F6F6 !important; 
  border-bottom:1px solid #eee;
}
.graph img.legend {
  display:none;
}

#dashboard .news .alert .body {
  border-bottom: none !important;
}


}
