css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("qdb.us") {

/**** front page | general ****/

body{
margin:0;
font-family:Sansation,"Trebuchet MS",Verdana,Vemana2000,Sans,sans-serif;
min-height:100%;
background-color:#111 !important;
color:#fff !important;
}
p{
color:#fff;
}
p.instructions{
background-color:#111 !important;
}
body>center{
background-color:#111;
}
tbody>tr[bgcolor="#c08000"],tr[valign="top"]>td[bgcolor="#c08000"]{
display:none;
}
a{
text-decoration:none !important;
color:#bbf !important;
}
a:hover{
text-decoration:underline !important;
}
.m{
font-family:inherit !important;
}
.m>a{
background-color:transparent !important;
font-size:inherit !important;
font-weight:normal !important;
font-family:inherit !important;
}
tr>td[colspan="3"]{
display:none;
}
td>small{
display:none;
}

/**** archive ****/

.cMH{
color:#fff !important;
}
table[cellpadding="5"],table[cellpadding="5"] *{
border:0px solid #111 !important;
}
.calendar td{
color:#444;
}
.calendarHeader td{
color:#aaa;
text-align:right !important;
}

/**** prefs ****/

label.ss{
font-family:inherit !important;
font-size:inherit !important;
}
input.button{
background-color:#888 !important;
}

/**** quote viewing ****/

form>table>tbody>tr>td.q{
background-color:transparent !important;
border:0 !important;
}
font[color="#0080c0"]{
color:#ddf !important;
}
font[color="#0000ff"]{
color:#88f !important;
}

/**** browse ****/

#page{
font-family:inherit !important;
}

/**** queue ****/

blockquote>tt{
font-family:Sansation,"Trebuchet MS",Verdana,Vemana2000,Sans,sans-serif;
font-weight:normal !important;
font-size:14px;
}

/**** voted ****/

body>center>div>p>span.tm{
background-color:inherit !important;
border:0 !important;
}

body>center>table>tbody>tr>td.m{
background-color:inherit !important;
}

}
