/* ==UserStyle==
@name         Minesweeper.online dark theme
@version      20211216.20.27
@namespace    userstyles.org/user/gertoperto
@description  Dark theme for minesweeper online (minesweeper.online)
Includes dark board
@author       gertoperto
@license      CC Zero
==/UserStyle== */

@-moz-document domain("minesweeper.online") {
body::-webkit-scrollbar,
div::-webkit-scrollbar {
    background-color: #333;
}

body::-webkit-scrollbar-thumb,
div::-webkit-scrollbar-thumb {
    background-color: #555;
    border: 4px solid #333;
    border-radius: 20px;
}

.premium-product-descr {
    color: #bbb;
}

.darkgray,
.glyphicon,
.ticket0 {
    color: white;
}

img[alt="Hero"]
{
    filter: brightness(4);        
}
    
img[alt="Survivor"],
img[alt="Account"],
.admin-icon,
.diff-icon {
    filter: invert(1);
}

a:hover {
    color: aqua;
}

.nav-tabs > li.active > a,
.nav-tabs > li.active > a:focus,
.nav-tabs > li.active > a:hover,
.nav-tabs > li > a:hover,
.nav-tabs > li > a:focus {
    color: aqua;
    background-color: #333;
    border: 1px solid #ddd;
    border-bottom-color: #ddd;
    cursor: default;
}

.close {
    color: aqua;
    text-shadow: 0 1px 0 black;
    opacity: .5;
}

.dropdown-menu>li>a,
a.active,
a,
table.chat-message > tbody > tr > td > span > a,
div.form-text > span > a,
table.chat-message > tbody > tr > td > span > span > a,
table.chat-message > tbody > tr > td > span > span > span > a,
table.chat-message > tbody > tr > td > span > span > nobr > a {
    color: aqua;
}

.level-select-link:hover {
    color: #fcf8e3;
}


.text-danger,
.new-quest-icon:not(.new-quest-icon-gray),
.event-pos-1,
.event-pos-1:active,
.event-pos-1:hover,
.event-pos-1:visited,
.level3-link,
.level3-link:active,
.level3-link:hover,
.level3-link:visited {
    color: orangered;
}

.event-pos-2,
.event-pos-2:active,
.event-pos-2:hover,
.event-pos-2:visited {
    color: orange;
}

.event-pos-3,
.event-pos-3:active,
.event-pos-3:hover,
.event-pos-3:visited {
    color: violet;
}

.event-pos-4,
.event-pos-4:active,
.event-pos-4:hover,
.event-pos-4:visited {
    color: skyblue;
}

.event-pos-5,
.event-pos-5:active,
.event-pos-5:hover,
.event-pos-5:visited {
    color: limegreen;
}

.event-pos-6,
.event-pos-6:active,
.event-pos-6:hover,
.event-pos-6:visited {
    color: #ccc;
}

.equipment-rarity-0,
.equipment-rarity-0:active,
.equipment-rarity-0:hover,
.equipment-rarity-0:visited {
    color: limegreen!important;
}

.equipment-rarity-1,
.equipment-rarity-1:active,
.equipment-rarity-1:hover,
.equipment-rarity-1:visited {
    color: skyblue!important;
}

.equipment-rarity-2,
.equipment-rarity-2:active,
.equipment-rarity-2:hover,
.equipment-rarity-2:visited {
    color: violet!important;
}

.equipment-rarity-3,
.equipment-rarity-3:active,
.equipment-rarity-3:hover,
.equipment-rarity-3:visited {
    color: orange!important;
}

.equipment-rarity-4,
.equipment-rarity-4:active,
.equipment-rarity-4:hover,
.equipment-rarity-4:visited {
    color: orangered!important;
}

.hint-free-absolute,
.text-success {
    color: limegreen;
}

.modal-body {
    background-color: #444;
}

.chat-gift,
.alert-warning,
.modal-footer,
.modal-header {
    background-color: #333;
}

.navbar-desktop {
    /* prevent the top of the svg from clipping out of bounds */
    padding-top: 0;
}

.brand-link .brand-text {
    text-shadow: none;
}

.brand-text,
.brand-link {
    /* this color thing is pretty self-explanatory */
    color: #fcf8e3;
    /* mitigate double shadow rendering */
    filter: drop-shadow(1px 1px #aaa);
}

.brand-logo {
    /* hide the original logo so the background becomes visible;
       this removes the original drop shadow, which will be restored in .brand-link */
    visibility: hidden;
}
.auth-buttons {
    /* cancel out the effect of the .navbar-desktop padding override */
    padding-top: 10px;
}
.brand-link {
    /* render drop shadow for svg background and associated text;
       this preserves the shadows from the original stylesheet */
    /* prevent the top of the svg from clipping out of bounds,
       and cancel out the effect of the .navbar-desktop padding override */
    padding-top: 10px;
    /* ensure new logo position matches that of the original */
    background-position: left 5px bottom 0;
    /* viewport of the original logo */
    background-size: 50px 50px;
    /* override default tiling behavior */
    background-repeat: no-repeat;
    /* this is a real mess but it was the only way i could get an svg background with no dependencies */
    background-image: url("data:image/svg+xml;base64,");
}

.hd_closed.hd_flag,
.hd_closed.hint-flag,
.xp_closed.xp_flag,
.xp_closed.hint-flag,
.nnhd_closed.nnhd_flag,
.nnhd_closed.hint-flag,
.rc_closed.rc_flag,
.rc_closed.hint-flag,
.al_closed.al_flag,
.al_closed.hint-flag,
.alr_closed.alr_flag,
.alr_closed.hint-flag,
.gs_closed.gs_flag,
.gs_closed.hint-flag,
.gsr_closed.gsr_flag,
.gsr_closed.hint-flag {
    filter: invert(1) hue-rotate(186deg) saturate(2);
}

.numbers-panel,
.top-area-center {
    filter: invert(100%);
}

.wrapper-border-left-bottom-wide,    
.wrapper-border-right-bottom-wide,
.wrapper-border-hor-wide,    
.top-area,
.wrapper-border-t-left,    
.wrapper-border-t-right,    
.wrapper-border-right-bottom,
.wrapper-border-left-bottom,    
.wrapper-border-left-top,
.wrapper-border-right-top,    
.wrapper-border-hor,
.wrapper-border-vert,
.hd_closed,
.xp_closed,
.nnhd_closed,
.rc_closed,
.al_closed,
.alr_closed,
.gs_closed,
.gsr_closed {
    filter: invert(100%);
}
    
    /*
.hd_check,
.xp_check,
.nnhd_check,
.rc_check,
.al_check,
.alr_check,
.gs_check,
.gsr_check {
    filter: invert(0%);
}*/
    
.hd_type0,
.xp_type0,
.nnhd_type0,
.rc_type0,
.al_type0,
.alr_type0,
.gs_type0,
.gsr_type0 {
    filter: invert(50%);
}

.navbar-desktop,
navbar-header {
    background-color: #333;
}
body {
    background-color: #444;
    color: #fcf8e3;
}
.main-content {
    background-color: #444;
}
.sidebar-nav ul li a {
    color: #fcf8e3;
}

a.level-select-link .active {
    color: #e9dda0;
}

.brand-link .brand-text,
a.level-select-link,
.nav-tabs>li>a {
    color: #fcf8e3;
}
label {
    color: white;
}
.result-block {
    border: 1px solid #e0e0e0;
    background-color: #333;
    color: #fcf8e3;
}

.spectate,
.btn-default,
input,
.pagination>.active>a,
.form-control,
.table th {
    background-color: #333;
    color: #fcf8e3;
}

.dropdown-menu,
.pagination>li>a,
.table td {
    background-color: #444;
    color: #fcf8e3;
}

.pagination>.disabled>a {
    background-color: #444;
    opacity: .5
}

.table td.bg-warning {
    background: #333;
}
.dotted-underline,
.dotted-underline:active,
.dotted-underline:hover,
.dotted-underline:visited {
    border-bottom: 1px dotted #fcf8e3;
}
.black-link,
.black-link:active,
.black-link:hover,
.black-link:visited {
    color: aqua;
}
.main-page h2 {
    color: #fcf8e3;
    margin-top: 0;
    font-size: 20px;
    font-weight: 700;
}
.help-content h3,
.help-content h4 {
    color: #fcf8e3;
}
    
.table td.bg-info {
    background: #555;
}    
    
.help-num1,
.help-num2,
.help-num3,
.help-num4,
.help-num5,
.help-num6,
.help-num7,
.help-num8 {
    background-color: #c0c0c0;
    padding-left: 5px;
    padding-right: 5px;
}
.thumbnail {
    background-color: #333;
}

.main-page h3 {
    color: #fcf8e3;
}

.popover-title {
    background-color: #333;
    color: #fcf8e3;
}

.popover {
    background-color: #333;
    color: #fcf8e3;
}

.storage-popover-table td,
.gems-popover-table td,
.score-popover-table td,
.player-popover-table td,
.equipment-popover-table td {
    background-color: #333!important;
}
}
