@-moz-document domain("inkscape.org") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

#menu > li > a
{
	text-transform: capitalize!important;
}

#insearch button,
#item-footer .tag,#item-footer,
#messages .error,#messages .info,
#sidecategory .tag,
#toc,
#topsearch button,

.comment-report,
.forum-pagination a.np-btn,
.galleryitem:hover,
.info .tog a,.info .tog,
.inline-attachments .inline,
.item .info,.item .side h2,.item .text,
.other-messages .error,.other-messages .info,

:focus,
div.pagination a,div.pagination span,
ul.pagination li a,ul.pagination li span
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

small,
.small
{
    font-size: 100%!important;
}

h2
{
	font-size: 31px!important;
}

#toc,
body
{
	font-size: 21px!important;
}

#shield .tabs > li > a,

.comment-count,
.comment-edit,
.comment-first-link,
.comment-remove,
.comment-report,
.forum-sub-header
{
    font-size: 20px!important;
}

li.comment blockquote
{
	font-size: 19px!important;
}

.item .text
{
	line-height: 25px!important;
}

/* Margins 
_____________________________________________________________________________________________________*/

#columns > .column,
#left-column,
#right-sidebar,

.article,
.galleryitem,

li.comment blockquote
{
	margin: 1.2rem!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

#columns > .column,
#left-column,
#right-sidebar,

.page
{
	padding: 1.2rem!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

selector
{
	max-height: none!important;
}

selector
{
	height: auto!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.galleryitem > p,

body
{
	max-width: none!important;
}

.galleryitem.nothing {
    width: 100%!important;
}

.forums-app,

body
{
	width: 98%!important;
}

#left-column {
    width: 72%!important;
}

#columns > .column {
    width: 25%!important;
}

}

@-moz-document domain("inkscape.org") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.info .tog p,
.table tr.announce,
.table tr.sticky,

a[href]:hover,
div.pagination a.current,
div.pagination span.current,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label,
ul.pagination li.active span
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.tutorial kbd.tutorial-mousebutton,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.comment-author .username,
.comment .details .title,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

em,
h3,
h4,
samp,
strong
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.postdate,

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.table-forum td.forum-no-results
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

li.comment blockquote
{
	color: #ffe4b9!important;
}

.token.selector, .token.attr-name, .token.string, .token.char, .token.builtin, .token.inserted {
	color: #af0!important;
}

.item .side dd
{
	color: #00b3ff!important;
}

.token.property, .token.tag, .token.boolean, .token.number, .token.constant, .token.symbol, .token.deleted
{
	color: #ff6cbe!important;
}

g {
    fill: rgb(255, 0, 0)!important;
    stroke: #fff!important;
}

path {
    color: rgb(18, 176, 173)!important;
    stroke: #fff!important;
}

text{
    fill: #F0E890!important;
	stroke: none!important;
}


}

@-moz-document domain("inkscape.org") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

#columns,
#content,
#insearch button,
#topsearch button,

body > .footer
{
	background-image: none;
}

/* Background 
_____________________________________________________________________________________________________*/

#item-footer,

.item .title,
.header,

body > .footer
{
	background-color: #060505!important;
}

.cke_wysiwyg_frame, .cke_wysiwyg_div,
.item .info
{
    background-color: #1a1a1a!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#columns h1,
#columns,
#content,
#insearch button,
#item-footer .tag,
#sidecategory .tag,
#toc,
#topsearch button,

.cke_inner,
.comment .details,.comment-header-group,.comment-header-group::before,.comment-report,.comment-time,
.forum-pagination a.np-btn,
.forums-search .sr-btn,
.galleryitem .label,.galleryitem:hover,
.img-circle,
.info .tog,
.inline-attachments .inline a,
.item .side h2,
.jumbotron,
.modal-breadcrumbs,
.nav-pills,

div.pagination a:hover,
ul.pagination li a:hover
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.btn-primary
{	
	background: radial-gradient(circle, #031017a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.btn-primary::before
{
	background: radial-gradient(circle, #000 0%, #031017 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.btn-primary:hover::before
 {
  opacity: 1!important;
}

/* Orange 
_____________________________________________________________________________________________________*/

.button,
.btn-default
{	
	background: radial-gradient(circle, #651c03a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.button::before,
.btn-default::before
{
	background: radial-gradient(circle, #000 0%, #651c03 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.button:hover::before,
.btn-default:hover::before {
  opacity: 1!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.btn-success
{	
	background: radial-gradient(circle, #022d01a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.btn-success::before
{
	background: radial-gradient(circle, #000 0%, #022d01 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.btn-success:hover::before {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

#content > div.wrapper,
#columns > .column,
#left-column,
#right-sidebar,

.comment,
.forum-content,
.forum-sidebar,
.modal-content,
.page
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.article,
.galleryitem,
.panel-footer
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

#columns > .column:hover,
#content > div.wrapper:hover,
#left-column:hover,
#right-sidebar:hover,

.article:hover,
.comment:hover,
.forum-content:hover,
.forum-sidebar:hover,
.galleryitem:hover,
.modal-content:hover,
.page:hover,
.panel-footer:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
_____________________________________________________________________________________________________*/

#messages .error,

.other-messages .error
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

/* Blue Gradient 
_____________________________________________________________________________________________________*/

#messages .info,

.other-messages .info
{
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
}

/*__ Announce Gradient _______________________________________________________________*/

.table tr.announce
{
	background: linear-gradient(to bottom, #e9d8f0 0%, #000 70%)!important;
}

/*__ Sticky Gradient _______________________________________________________________*/

.table tr.sticky
{
	background: linear-gradient(to bottom, #dff0d8 0%, #000 70%)!important;
}

/*__ Unread Gradient _______________________________________________________________*/

.table-forum .topic-item > a:not(.topic-jump-unread):hover
{
	background: linear-gradient(to bottom, #da0404 0%, #000 70%)!important;
}


/* Code 
_____________________________________________________________________________________________________*/

.tutorial kbd.tutorial-keycombo,

pre
{
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
	background-size: 100%!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

#sidecategory li:not(.active):hover,

.nav-pills>li.active>a,
.nav-pills>li.active>a:focus,
.nav-pills>li.active>a:hover,
.table-hover>tbody>tr:hover,

ul.labels-info a:focus,
ul.labels-info a:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}


}

@-moz-document domain("inkscape.org") {
/* Image Settings */

#sidecategory li:not(.active),

.article,
.comment,
.galleryitem,
.nav-stacked>li+li,
.table-hover>tbody>tr
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

#sidecategory li:not(.active):hover,

.article:hover,
.comment:hover,
.galleryitem:hover,
.nav-stacked>li+li:hover,
.table-hover>tbody>tr:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/


img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/


img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*
_____________________________________________________________________________________________________*/

selector:hover
 {
	opacity: 1!important;
}

}

