:root {
--background-image: url('https://i.imgur.com/3jaGqlk.jpg');
--background-image-blur: 14px;
--background-image-size: contain;
--background-image-position: center;
--popout-modal-image: url('https://i.imgur.com/kYW2H5C.jpg');
--popout-modal-blur: 100px;
--popout-modal-size: cover;
--popout-modal-position: center;
--home-button-image: url('https://discordstyles.github.io/FrostedGlass/assets/discord.svg');
--home-button-size: cover;
--home-button-position: center;
--serverlist-brightness: 0;
--left-brightness: 0.1;
--middle-brightness: 0.1;
--right-brightness: 0.05;
--popout-modal-brightness: 0;
--gradient-primary: 55,169,183;
--gradient-secondary: 20,56,181;
--gradient-direction: 320deg;
--tint-colour: 162,198,255;
--tint-brightness: 0.1;
--window-padding: 11px;
--window-roundness: 30px;
--scrollbar-colour: #ffffff0d;
--link-colour: #6589F4FF;
--show-gift-gif-buttons: none;
--font: 'Nunito';
--update-notice-1: none;
}
