css:
@-moz-document url-prefix("https://live.bilibili.com/") {
.name {
	width:180px !important;
}
.fans-medal-item {
	display:none !important;
}
.item .uname a {
	width:180px !important;
}
.gift-control-section {
	display:none !important;
}
.flip-view {
	display:none !important;
}
.room-feed {
	display:none !important;
}
.link-footer {
	display:none !important;
}
.shortcuts-ctnr {
	display:none !important
}
.right-ctnr div:nth-child(1) {
	display:none !important
}
.right-ctnr div:nth-child(2) {
	display:none !important
}
#sections-vm {
	display:none !important
}
.lower-row {
	display:none !important
}
.live-area {
	display:none !important
}
.hot-rank-text {
	display:none !important
}
.head-info-section {
	height:70px !important
}
.header-info-ctnr {
	padding:5px 17px 5px 17px !important
}
.face-pendants {
	width:48px !important;
	height:48px !important;
}
.avatar {
	margin-top:5px !important;
	width:48px !important;
	height:48px !important;
}

.live-title .text{
    font-size: 16px;
    line-height: 60px !important;
}
}
