css:
@-moz-document domain(myanimelist.net) {
  :root {
    --entry-height: 35px;
    --entry-width: 177px;
  }

  .favmore {
    display: inline-block;
  }

  .btn-favmore,
  .fav-slide-block .btn-fav-slide-side.right,
  .fav-slide-block .btn-fav-slide-side.right {
   display: none;   
  }

  .fav-slide-block {
    width: calc(var(--entry-width) + 20px);
    display: inline-block;
    vertical-align: top;
  }

     
  .fav-slide-block .fav-slide-outer {
    height: unset;
    width: var(--entry-width);
  }
      
  .fav-slide-outer .fav-slide {
    display: flex;
    flex-direction: column;
    width: unset !important;
    margin-top: 20px;
  }

  .fav-slide-block .fav-slide .btn-fav {
    width: var(--entry-width);
    padding: 3px 0;
    border-bottom: 1px solid #ebebeb;
  }

  .fav-slide-block .fav-slide .btn-fav:last-of-type {
    border-bottom: 0;
  }

  .fav-slide-outer .fav-slide .btn-fav .link {
    height: var(--entry-height);
    width: unset;
  }

  .fav-slide-block .fav-slide .btn-fav .link.bg-center {
    position: relative;
  }


  .fav-slide-block .fav-slide .btn-fav .link .title {
    background: none;
    color: black;
    text-shadow: unset;
    line-height: unset;
    padding: 0;
    display: inline-block;
    vertical-align: top;
    position: absolute;
    top: 1px;
    white-space: nowrap;
    margin-left: 30px;
    overflow: hidden;
    text-overflow: ellipsis;
    width: calc(var(--entry-width) - 35px);
    font-size: 11px !important;
    opacity: 1;
  }

  .fav-slide-block .fav-slide .btn-fav .link img {
    width: 25px;
    height: var(--entry-height);
    background: transparent;
  }


  .page-common .content-container .container-right h5 {
    width: calc(var(--entry-width));
    font-size: 12px;
    display: inline-block;
    position: absolute;
  }

  .page-common .content-container .container-right .user-statistics h5 {
    display: block;
    position: static;
    width: initial;
  }



  .fav-slide-block .fav-slide .btn-fav .link .users {
    background: transparent;
    color: #aaa;
    text-shadow: unset;
    top: 14px;
    left: 25px;
    opacity: 1;
    right: unset;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    width: calc(var(--entry-width) - 35px);
  }
}
