/* Позиция и контур плавающего Видео-Поиска */
[dir] .VideoSearchInput--sticky.ui_search_new .ui_search_input_block {
    border-radius: 0; margin-top: -6px; box-shadow: inset 0px -1.5px 0 0 rgba(128, 128, 128, .3), 0 1px 3px rgba(0, 0, 0, .5) !important;}
/* Всплывающее меню недавних Поисков */
._ui_search_sugg_list.ui_search_sugg_list {border-radius: 0px 0px 10px 10px !important; 
    box-shadow: 0 2px 5px rgba(0, 0, 0, .5) !important; margin-top: 0px !important;}
/* ======== конец раздела ======== */





/* ######################## Меню "=" ######################## */

/* Позицианирование Заголовков Меню */
:lang(ru) #l_msg .left_label:before {content: "Мои Сообщения"; display: inline-block; padding-right:260px !important/*70px*/;margin-left:1px;} /* месенджер (!) */
:lang(ru) #l_gr .left_label:before {content: "Мои Группы"; display: inline-block; padding-right:240px !important/*70px*/;margin-left:0px;} /* Сообщества (!) */
/* Счётчик Уведомлений (позиционирование) */
[dir] body.new_header_design .left_count_wrap:lang(ru) {margin-right: 0px !important; margin-bottom: 0px;}
[dir] body.new_header_design .left_count_wrap:lang(en) {margin-right: 0px !important;}
[dir] .PortalNavigationDropdown .left_count_wrap .left_count {padding: 0px 6px 0px 6px; margin-top: 0px;}

/* Цвет текста Пунктов Левого меню */
.inl_bl.left_label {color: #2A5885;}
/* Цвет Подсвеченного Пункта Левого меню */
[dir] .PortalNavigationDropdown .left_row:hover {background: #F0F2F5;}
/* Тень всплывающего Левого Меню */
[dir] .PortalNavigationDropdown {
    margin-top: -7px;
    border-radius: 0 0 4px 4px;
    box-shadow: 0 1px 5px rgba(0, 0, 0, .5) !important;}


/* Цыет Решётки Меню *=* */
[dir] .PortalNavigation__icon {
   filter: brightness(200%) contrast(200%); margin-top: -2px;}
/* Цыет Фона Решётки Меню *=* - подсвеченный */
[dir] .PortalNavigation:hover, [dir] .PortalNavigation--expanded {height: 43px; background-color: #416A9A;}
/* ================== конец раздела =================== */




/* ######################## ЗАГОЛОВКИ ######################## */

/* Звет Шапки с заголовками */
-o-[dir] .page_block_header {border-radius: 4px 4px 0 0 !important; background: #F0F2F5 !important;}
-o-[dir=ltr] .VideoHeader__title {border-radius: 4px 4px 0 0; background: rgba(214, 220, 228, .37) !important;}

/* = = = Заголовки (Плэйлистов / Имени Пользователя) = = = */

/* Смещение влево Названия Плейлиста  */
.VideoInfoPanel--playlist .VideoInfoPanel__contentInfo {margin-left: -60px;}
/* Смещение влево Имени Пользователя */
.VideoInfoPanel__contentInfo {margin-left: -40px;}
/* Толщина Названия Плейлиста / Имени Пользователя */
.VideoInfoPanel__title {font-weight: 600;}
/* = = = = = = = = = = = = = = = = = = = = = = = = = = = = = */

/* Выделение Болдом названия активного раздела */
div.ui_crumb, .VideoHeader__title {font-weight: bold; font-size: 14px; position: relative; top: 0px; color: rgb(104, 104, 104);}


/* Отступы сверху заголовков Плейлистов */
div.VideoInfoPanel--withTopDelimiter.VideoInfoPanel--playlist.VideoInfoPanel {margin: -20px 0;}
/* Отступы сверху заголовков Плейлистов - прочих*/
[dir] .VideoInfoPanel--withTopDelimiter:not(:first-child) {
    -o-border-top: 1px solid  #D9DADC /* var(--separator_alternate)*/;
    padding-top: 0px;
    margin-top: 0;}
/* Отступы снизу Плейлистов и Миниатюр Видео */
.VideoCard__infoContent {padding-bottom: 5px;}
/* Отступы снизу Миниатюр Видео/Фильмов */
.VideoCard--verticalThumbs.js-video_playlist_item.VideoCard--playlist .VideoCard__info > .VideoCard__infoContent
{padding: 0 8px 6px 8px !important;}
/* -o-.VideoPlaylistItem--noVideosCount.VideoCard--verticalThumbs.js-video_playlist_item.VideoCard--playlist.VideoCard > .VideoCard__info > .VideoCard__infoContent 
{padding: 0 8px 6px 8px} */



/* Отступы сверху заголовка внутри Плейлиста */
.VideoInfoPanel--playlistPage.VideoInfoPanel--playlist.VideoInfoPanel {margin: -20px 0;} /*v1*/
.VideoInfoPanel--playlistPage.VideoInfoPanel--playlist.VideoInfoPanel {margin: 0px 0 -40px 0;} /*v2*/


/* Отступы сверху заголовка - Мои Видео (и внутри Плейлиста) */
.VideoInfoPanel {margin: 0px 0 -40px 0; position: relative; top: -20px;}
/* Компенсация Сдвига Заголовков Плейлистов (от Отступы сверху заголовка - Мои Видео) */
div.VideoInfoPanel--withTopDelimiter.VideoInfoPanel--playlist.VideoInfoPanel {position: relative; top: 0px;}

/* отступы Пунктов (Все / Интервью и шоу / Туризм - и тд. ) */
[dir] .VideoTabsSlider .VideoTabs {padding: 0 10px;}
-o-[dir] .VideoTabsSlider .VideoTabs {border-top: 6px solid #fff;} /* бордюр сверху */
/* Цвет Заголовка с Пунктами */
.VideoTabs.clear_fix.ui_tabs {background: #F0F2F5 !important; width: fit-content; -o-margin: 6px 0 0px 0;}
/* Отступы от заголовка */
#video_block_header > .VideoHeader {margin: 0px 0 6px 0;}
/* Отступы от заголовка - Первый из всех + Смеещние Аватара Пользователя */
[dir] .VideoInfoPanel:first-child {margin-top: -6px; margin-left: 16px;}
/* доп. Позицианирование превью Плейлиста и Аватара Пользователя */
[dir] .VideoInfoPanel__cover {left: -16px;}

/* отступы Пунктов (Добавленные / Загруженные) */
[dir] .VideoTabs--withMargin.ui_tabs {padding: 0 10px;}
/* отступы Пунктов (обзор комментариев / самые новые) */
.video_tab_actions_wrap {padding: 0 10px;}

/* ================== конец раздела =================== */





/* ################ Правое Рабочее пространство ################ */

/* Отступы Видимой рабочей оболасти страницы */
.VideoLayout__main {margin-left: -20px; padding: 24px 100px 0px 20px!important;}

-o-[dir] .VideoLayout__main {
    padding: 24px 184px 0;}

-o-.VideoLayout__main {
    --aside-column-width: 80px !important;
    -o---aside-transition-func: cubic-bezier(0.65, 0.05, 0.36, 1);
    display: flex;}

-o-.VideoLayout {
    --aside-column-width: 280px;
    --aside-transition-func: cubic-bezier(0.65, 0.05, 0.36, 1);
    display: -ms-flexbox;
    display: flex;
}


/* ширина Рабочего пространства */
.video_showcase {
    --layout-width: 100% !important;        /* --layout-width: 100% */
    --layout-min-width: 1000px !important;  /* --layout-min-width: 1000px */
    --layout-max-width: 1500px !important;} /* --layout-max-width: 2280px */



/* ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ Всплывающие меню рабочей области  ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ */

/* Всплывающе меню (...) на Миниатюрах Видео */
[dir] .video_showcase .tt_w:not(.tt_black) {
    border-radius: 4px;
    border: 1px solid rgba(0, 0, 0, .35); /* #bec2c6; */
    box-shadow: 0 1px 3px rgba(0, 0, 0, .3);
    /* box-shadow: 0 0 4px rgba(0,0,0,.04), 0 4px 32px rgba(0,0,0,.16); */}

/* Подсвеченный Пункт - Всплывающего меню (...) на Миниатюрах Видео */
[dir] .VideoCardMenu__item:hover {background-color: #e7e9ec;}


/* Всплывающе меню (...) Плейлистов */
[dir] .VideoInfoPanel__dropdown {
    background-color: var(--background_content);
    border-radius: 8px;
    padding: 8px 0;
    border: 1px solid #bec2c6;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .3);
    /* box-shadow: 0 0 2px rgba(0,0,0,.08), 0 8px 24px rgba(0,0,0,.08); */}

/* Подсвеченный Пункт - Всплывающего меню (...) Плейлистов */
[dir] .VideoInfoPanel__dropdownItem:hover {
    cursor: pointer;
    background-color: #e7e9ec;}


/* Меню Сортировки Роликов пользователя */
[dir] .video_showcase .idd_popup {
    border: 1px solid #bec2c6;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .3);
    border-radius: 6px;}
/* Подсвеченный Пункт - Меню Сортировки Роликов пользователя */
[dir] .video_showcase .idd_popup .idd_item.idd_hover {background-color: #e7e9ec;}
/* Активный Пункт - Меню Сортировки Роликов пользователя */
[dir] .video_showcase .idd_popup .idd_item.idd_hl {background-color: rgba(217, 228, 240, .99);}


/* более явное подсвечивание иконки (...) на миниатюре Видео - Подсвеченное */
.VideoCard__menuToggle:hover .VideoCard__menuToggleIcon svg {
    color: rgb(0, 0, 0) /* var(--icon_secondary) #99a2ad */;
    border-radius: 20%;
    background: rgba(255, 255, 255, .7);
    -o-opacity: 1;
    -o-box-shadow: inset 0 0 1px 1px #0095ff;
    -o-filter: drop-shadow(0px 0px 0.7px #808080) drop-shadow(0px 0px 0.7px #808080);}
/* более явное подсвечивание иконки (...) на миниатюре Видео */
.VideoCard__menuToggleIcon svg {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 24px;
    height: 24px;
    color: #626262 /* var(--icon_tertiary) #aeb7c2 */;
    filter: drop-shadow(0px 0px 1px #fff);
    transition: color .15s;
    pointer-events: none;
    -o-opacity: 1;}

/*  ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ ~ */


/* ======================== Слейдеры и стрелки Плейлистов ============================ */

/* слайдер Плейлистов */
-o-[dir=ltr] .PlaylistsSlider {
    margin-left: -24px;
    margin-right: -24px;
    padding-left: 24px;
}
/* Мягкий Градиент по бокам Слайдера Плейлистов */
[dir=ltr] .PlaylistsSlider:before 
{display: block; content:''; position: absolute; left: 0; top: 0px; z-index: 9; color: red; 
width: 24px; height: 100%; background: linear-gradient(to right, rgba(255,255,255,1) 0%,rgba(255,255,255,1) 58%,rgba(255,255,255,1) 59%,rgba(255,255,255,0) 100%);}
[dir=ltr] .PlaylistsSlider:after
{display: block; content:''; position: absolute; right: 0; top: 0px; z-index: 9; color: red; 
width: 24px; height: 100%; background: linear-gradient(to left, rgba(255,255,255,1) 0%,rgba(255,255,255,1) 58%,rgba(255,255,255,1) 59%,rgba(255,255,255,0) 100%);}

.js-video-slider.PlaylistSlider:before 
{display: block; content:''; position: absolute; left: 0; top: 0px; z-index: 9; color: red; 
width: 24px; height: 100%; background: linear-gradient(to right, rgba(255,255,255,1) 0%,rgba(255,255,255,1) 58%,rgba(255,255,255,1) 59%,rgba(255,255,255,0) 100%);}
.js-video-slider.PlaylistSlider:after
{display: block; content:''; position: absolute; right: 0; top: 0px; z-index: 9; color: red; 
width: 24px; height: 100%; background: linear-gradient(to left, rgba(255,255,255,1) 0%,rgba(255,255,255,1) 58%,rgba(255,255,255,1) 59%,rgba(255,255,255,0) 100%);}

/* Стрелки - Влево <- / -> Вправо */

