.js-video-slider.VideoTabsSlider.ui_gallery.ui_gallery .ui_gallery__arrow.ui_gallery__arrow_right 
{transform: scale(0.8, 1.5) !important; margin-top: 0px; margin-right: -7px;}
/* ================================================================================== */


/* ========================= конец раздела ============================== */




/* ####################################### Левая Панель ####################################### */

/* Ширина Левой Панели страницы */
.VideoLayout__asideContent {min-width: 60px; max-width: 260px;}

