@-moz-document url-prefix("https://vk.com/video") {
/* ############ Шапка ############ */

/* Цвет ТЕКСТа Логотипа "VK Видео" - .VKVideoLogo > svg > g > path */
.VKVideoLogo {
    display: none;
    width: 110px;
    height: 24px;
    color: #fff !important;}

/* Выделение Логотипа "VK Видео" - .VKVideoLogo > svg > path */
.VKVideoLogo svg {filter: drop-shadow(1px 0px 1px rgba(0, 0, 0, .7)) !important;}
.VKVideoLogo > svg > path:nth-of-type(1) {fill: #0095ff !important; fill-rule: nonzero; 
    -o-stroke: #000; stroke-width: 0.5; stroke-linecap: round; stroke-linejoin: round;}
/* ======== конец раздела ======== */
/* №№№№№№№№№№№№№№№ Разделы VK Видео №№№№№№№№№№№№№№№ */

/* тут можно отключить отдельные разделы Видо-каталога. 
просто сотрите заглушку "-o-" перед ненужным разделом, чтобы 
отключить раздел, или допишите её вновь, чтобы включить его снова*/

-o-[href="/video/at_home"] {display: none !important;}

/* Трансляции */
-o-[href="/video/lives"] {display: none !important;}

/* Спорт */
-o-[href="/video/sport"] {display: none !important;}

/* Фильмы и сериалы */
[href="/video/movies"] {display: none !important;}

/* ТВ-шоу */
[href="/video/tvshow"] {display: none !important;}

/* Детям */
[data-title="Детям"] {display: none !important;}

/* Подписки */
-o-[href="/video/subscriptions"] {display: none !important;} 
/* №№№№№№№№№№№№№№№№№№№№№№№№№№№№№№№№№№№№№№№№№№№№№ */

/* убрать ВСЁ Дерьмо из Видеокаталога: 
    none - убрать  
    flow - показать */
.videocat_featured, #videocat_other_blocks
  {display: none !important}
  }
