css:
/* i really want this to be global */
 .fav-slide-block .fav-slide {
     display: table-cell;
     width: 100px !important;
}
 .fav-slide-block .fav-slide .btn-fav {
     padding-bottom: 10px !important;
}
 .fav-slide-block .fav-slide-outer {
     height: auto;
     width: 200px !important;
}
 .fav-slide-block .fav-slide .btn-fav .link img {
}
 #anime_favorites{
     height: auto !important;
     width: 198px;
     position: relative !important;
     position: relative;
     float: left;
}
 #anime_favorites:hover{
     overflow-y: auto;
}
 div#manga_favorites.fav-slide-block.mb12 {
     height: auto !important;
     width: 198px;
     position: relative !important;
     scrollbar-width: thin;
     padding-left: 2px;
     position: relative;
     float: left;
}
 div#manga_favorites.fav-slide-block.mb12:hover{
     overflow-y: auto;
}
 #character_favorites{
     height: auto !important;
     width: 198px;
     position: relative !important;
     scrollbar-width: thin;
     padding-left: 2px;
     position: relative;
     float: left;
}
 #character_favorites:hover{
     overflow-y: auto;
}
 #person_favorites {
     height: auto !important;
     width: 198px;
     position: relative !important;
     scrollbar-width: thin;
     padding-left: 2px;
     position: relative;
     float: left;
}
 #person_favorites:hover{
     overflow-y: auto;
}
 .fav-slide-block .fav-slide{
     position: relative;
}
 .favmore{
     display: block !important;
}
 .container-right > h5:nth-child(6){
     position: absolute;
     left: 450px;
     margin-top: -25px !important;
}
 .favmore > h5:nth-child(1){
     position: absolute;
     left: 850px;
     margin-top: -25px !important;
}
 .container-right > h5:nth-child(8){
     position: absolute;
     left: 650px;
     margin-top: -25px !important;
}
 .btn-favmore.open, .btn-favmore, .fav-slide-block .btn-fav-slide-side {
     opacity: 0 !important;
     pointer-events: none;
}
 .page-common .content-container .pt24 {
     position: relative;
     float: left;
     top:-60px;
}
 .fav-slide-block .lazyloaded{
     width: 40px;
     height: 60px;
}
 .fav-slide-block .lazyloaded{
     width: 40px;
     height: 60px;
     image-rendering: inherit;
     image-rendering: -webkit-optimize-contrast;
}
 .fav-slide-block .fav-slide .btn-fav .link .title {
     left: 0;
     width: 120px;
}
 .fav-slide-block .fav-slide .btn-fav .link {
     height: 55px;
}
 .fav-slide-block .fav-slide .btn-fav .link .users {
     left: 0;
     margin-left: 50px;
     top: 30px;
     width: 100px;
}
 .fav-slide-block .fav-slide .btn-fav .link .title:hover{
     text-decoration: underline;
}
/* UPDATE REPAIRS */
 .fav-slide-block .fav-slide .btn-fav .link .title {
     opacity: 1;
}
/* UPDATE REPAIRS FOR COMPANIES */
 h5:nth-child(3) {
     width: 100px;
}
 #company_favorites {
     height: auto !important;
     width: 198px;
     position: relative !important;
     scrollbar-width: thin;
     padding-left: 2px;
     float: left;
}
 #company_favorites:hover {
     overflow-y: auto;
}
 #company_favorites .fav-slide-outer .link {
     align-items: center;
     -webkit-box-align: center;
     display: -webkit-box;
     display: -ms-flexbox;
     display: flex;
     -ms-flex-align: center;
     width: 150px;
}
 #company_favorites .link .title {
     top: -30px;
}
 .container-right > h5:nth-child(6) {
     border-bottom: 0 !important;
}
 .container-right > h5:nth-child(8) {
     border-bottom: 0 !important;
}
 .favmore > h5:nth-child(1) {
     border-bottom: 0 !important;
}
/* FAVORITES HEIGHT Change auto to the new px amount, 1500px is the default for 10 faves. */
 .fav-slide-block .fav-slide-outer {
     height: auto !important;
}
/* EXTRA INFO Change the zero after opacity to 1 to see the extra info at all times.*/
 .fav-slide-block .fav-slide .btn-fav .link .users {
     opacity: 0;
}
/* TITLES/NAMES Remove these 2 sections of code starting with ".fav-slide" to move the title/name back to the pic. */
 .fav-slide-block .fav-slide .btn-fav .link .title {
     background: transparent !important;
}
 .fav-slide-block .fav-slide .btn-fav .link .title {
     color: #1d439b;
     top: -15px;
     text-shadow: none !important;
     font-size: 11px;
     left: 42px;
     font-family: Verdana,Arial;
     font-size: 11px !important;
}
 
 
