css:
@-moz-document domain("music.youtube.com") {
/*V1.0.2_230209*/
/*userinteractive
--*/
/*var*/
:root {
    --minr:/*[[min]]*/;
    --acnr:/*[[acn]]*/;
    --txnr:/*[[txn]]*/;
    --scnr:/*[[scn]]*/
}
/*miniplayer_mpv*/
ytmusic-player[player-ui-state_=MINIPLAYER] {
/*[[mpv]]*/
    /*[[msv]]*/
}
/*brpfp_brv*/
 ytmusic-thumbnail-renderer[thumbnail-crop_=MUSIC_THUMBNAIL_CROP_CIRCLE] .image.ytmusic-thumbnail-renderer, ytmusic-player[player-ui-state_=MINIPLAYER], .yt-simple-endpoint, .yt-spec-touch-feedback-shape--touch-response .yt-spec-touch-feedback-shape__stroke, ytmusic-chip-cloud-chip-renderer[enable-bauhaus-style][chip-style=STYLE_LARGE_TRANSLUCENT_AND_SELECTED_WHITE] a.ytmusic-chip-cloud-chip-renderer {
    /*[[icv]]*/
}
/*stylesheetYT
--*/
html:not(.style-scope) {
    --ytmusic-color-black1:var(--scnr);
    --ytmusic-color-black4:var(--minr);
    --ytmusic-color-white1-alpha70:var(--txnr);
    --ytmusic-color-youtubered:var(--acnr);
    --ytmusic-color-white1:var(--txnr);
    --yt-button-color:var(--acnr)
}
.title.ytmusic-two-row-item-renderer {
    --yt-endpoint-visited-color:var(--txnr);
    --yt-endpoint-hover-color:var(--acnr);
}
.subtitle.ytmusic-two-row-item-renderer, .third-title.ytmusic-two-row-item-renderer {
    --yt-endpoint-color:var(--ytmusic-overlay-text-secondary)
}
.dropdown-content.ytmusic-dropdown-renderer {
    --paper-listbox-background-color:var(--scnr)
}
#top-level-buttons.ytmusic-menu-renderer>.outline-button.ytmusic-menu-renderer, .edit-playlist-button.ytmusic-menu-renderer, ytmusic-toggle-button-renderer.ytmusic-menu-renderer {
    --yt-button-color:var(--txnr)
}
tp-yt-paper-slider {
    --paper-progress-active-color:var(--paper-slider-active-color)
}
ytmusic-player-bar[is-color-sampling-enabled] #progress-bar.ytmusic-player-bar {
    --paper-slider-active-color:var(--acnr)
}
#progress-bar.ytmusic-player-bar, ytmusic-player-bar[is-color-sampling-enabled] #progress-bar.ytmusic-player-bar[focused], ytmusic-player-bar[is-color-sampling-enabled]:hover #progress-bar.ytmusic-player-bar {
    --paper-slider-knob-color:var(--acnr)
}
.volume-slider.ytmusic-player-bar, .expand-volume-slider.ytmusic-player-bar {
    --paper-slider-active-color:var(--acnr);
    --paper-slider-knob-color:var(--acnr);
    
}
ytmusic-player-bar[is-color-sampling-enabled] #progress-bar.ytmusic-player-bar {
    --paper-slider-container-color:transparent;
    --paper-slider-secondary-color:var(--acnr)
}
tp-yt-paper-tabs.ytmusic-player-page {
    --paper-tabs-selection-bar-color:var(--acnr)
}
tp-yt-paper-slider {
    --paper-progress-active-color: var(--acnr);
}
/*stylesheetZY
--*/
/*scroll*/
html:not(.style-scope)::-webkit-scrollbar-track {
    background-color:var(--minr)!important;
    border:transparent!important
}
html:not(.style-scope)::-webkit-scrollbar-thumb, ytmusic-multi-select-menu-renderer:not([is-mobile-web]) .scroller.ytmusic-multi-select-menu-renderer::-webkit-scrollbar-thumb, ytmusic-player-page:not([is-mobile-web]) .scroller.ytmusic-player-page::-webkit-scrollbar-thumb {
    background-color:var(--acnr)!important;
    border-radius:5rem!important;
    border:.2rem solid var(--minr)
}
/*min*/
body, #suggestions.ytmusic-search-suggestions-section>.ytmusic-search-suggestions-section:hover, #suggestions.ytmusic-search-suggestions-section>.selected-suggestion.ytmusic-search-suggestions-section, ytmusic-multi-select-menu-item-renderer:hover, yt-button-renderer.watch-button.ytmusic-menu-renderer, #items.ytmusic-menu-popup-renderer>.ytmusic-menu-popup-renderer:hover {
    background-color:var(--minr)
}
/*acntxn*/
ytmusic-pivot-bar-item-renderer:hover, ytmusic-pivot-bar-item-renderer.iron-selected, ytmusic-search-box:not([opened]):hover, ytmusic-item-section-tab-renderer.iron-selected .tab.ytmusic-item-section-tab-renderer, .tab.selected.ytmusic-item-section-tab-renderer, .content-info-wrapper.ytmusic-player-bar .title.ytmusic-player-bar, tp-yt-paper-tab.iron-selected.ytmusic-player-page, .song-title.ytmusic-player-queue-item {
    color:var(--acnr) 
}
/*acnbrd*/
#nav-bar-divider.ytmusic-app-layout, #title.ytmusic-multi-select-menu-renderer, ytmusic-menu-item-divider-renderer, ytmusic-item-section-tab-renderer.iron-selected .tab.ytmusic-item-section-tab-renderer, .tab.selected.ytmusic-item-section-tab-renderer, .slider-knob-inner.tp-yt-paper-slider, ytmusic-tabs.iron-selected .tab.ytmusic-tabs, .tab.selected.ytmusic-tabs {
    border-color:var(--acnr);
}
/*acnbrd2*/
yt-button-renderer.watch-button.ytmusic-menu-renderer, ytmusic-player-queue-item[play-button-state=loading], ytmusic-player-queue-item[play-button-state=playing], ytmusic-player-queue-item[play-button-state=paused] {
    border:.2rem solid var(--acnr)
}
/*acnbcg*/
ytmusic-dropdown-item-renderer[is-selected], .slider-knob-inner.tp-yt-paper-slider {
    background-color:var(--acnr);
    animation:bcgrgb 10s infinite
}
/*txn*/
ytmusic-pivot-bar-item-renderer, ytmusic-search-box, ytmusic-search-suggestion, ytmusic-search-suggestion[is-color-sampling-enabled] #delete.ytmusic-search-suggestion, .title.ytmusic-carousel-shelf-basic-header-renderer, yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string, .title.ytmusic-two-row-item-renderer, yt-icon.ytmusic-navigation-button-renderer, .container.ytmusic-custom-index-column-renderer, .title.ytmusic-header-renderer, button.ytmusic-sort-filter-button-renderer, .text.ytmusic-menu-title-renderer, .text.ytmusic-multi-select-menu-item-renderer, .title.ytmusic-responsive-list-item-renderer, a.ytmusic-icon-link-renderer, .tab.ytmusic-item-section-tab-renderer, .label.ytmusic-dropdown-item-renderer, .title.ytmusic-detail-header-renderer, .text.ytmusic-menu-navigation-item-renderer, .text.ytmusic-menu-service-item-renderer, ytmusic-player-bar[is-color-sampling-enabled] .time-info.ytmusic-player-bar, .text.ytmusic-toggle-menu-service-item-renderer, ytmusic-player-queue-item[is-color-sampling-enabled] .duration.ytmusic-player-queue-item, ytmusic-player-queue-item[is-color-sampling-enabled] .byline.ytmusic-player-queue-item, .autoplay.ytmusic-tab-renderer .title.ytmusic-tab-renderer, .autoplay.ytmusic-tab-renderer .subtitle.ytmusic-tab-renderer, ytmusic-chip-cloud-chip-renderer[enable-bauhaus-style][chip-style=STYLE_LARGE_TRANSLUCENT_AND_SELECTED_WHITE] a.ytmusic-chip-cloud-chip-renderer, ytmusic-tabs.iron-selected .tab.ytmusic-tabs, .tab.selected.ytmusic-tabs, .tab.ytmusic-tabs
 {
    color:var(--txnr)
}
/*txnfill*/
tp-yt-iron-icon, .icon.ytmusic-menu-navigation-item-renderer, .icon.ytmusic-menu-service-item-renderer, .icon.ytmusic-toggle-menu-service-item-renderer {
    fill:var(--txnr)
}
/*scn*/
.previous-items-button.ytmusic-carousel, .next-items-button.ytmusic-carousel, button.ytmusic-navigation-button-renderer, button.ytmusic-sort-filter-button-renderer, ytmusic-dropdown-renderer[dropdown-style=default] {
    background-color:var(--scnr)
}
/*tra*/
ytmusic-pivot-bar-item-renderer:hover, ytmusic-search-box:not([opened]):hover, #nav-bar-divider.ytmusic-app-layout, ytmusic-multi-select-menu-item-renderer:hover{
    transition:.5s ease
}
/*rbrd*/
ytmusic-search-box[opened], ytmusic-search-box[has-query], ytmusic-search-suggestions-section.ytmusic-search-box, button.ytmusic-sort-filter-button-renderer, #container.ytmusic-multi-select-menu-renderer, #end-items.ytmusic-item-section-tabbed-header-renderer, ytmusic-dropdown-renderer[dropdown-style=default], #top-level-buttons.ytmusic-menu-renderer>.outline-button.ytmusic-menu-renderer, .edit-playlist-button.ytmusic-menu-renderer, ytmusic-toggle-button-renderer.ytmusic-menu-renderer, #contents.ytmusic-playlist-shelf-renderer>*.ytmusic-playlist-shelf-renderer:not(:last-child), tp-yt-paper-listbox.ytmusic-menu-popup-renderer, ytmusic-player-queue-item {
    border:transparent
}
/*brstandard*/
ytmusic-two-row-item-renderer[aspect-ratio=MUSIC_TWO_ROW_ITEM_THUMBNAIL_ASPECT_RATIO_SQUARE] .image.ytmusic-two-row-item-renderer {
    border-radius: 0
}
/*saer
--*/
#secondaryProgress.tp-yt-paper-progress {
    opacity:.25
}
ytmusic-background-overlay-renderer#background, ytmusic-thumbnail-renderer[thumbnail-crop_=MUSIC_THUMBNAIL_CROP_CIRCLE]:hover, ytmusic-thumbnail-renderer[thumbnail-crop_=MUSIC_THUMBNAIL_CROP_CIRCLE]:hover .image.ytmusic-thumbnail-renderer:hover, button.ytmusic-sort-filter-button-renderer:hover, ytmusic-dropdown-renderer[dropdown-style=default]:hover {
    --ytmusic-background-overlay-background:linear-gradient(135deg,var(--acnr),rgba(0,0,0,0),rgba(0,0,0,0))!important;
}
ytmusic-player-queue-item[play-button-state=playing], ytmusic-player-queue-item[play-button-state=paused] {
box-shadow:0 0 .8ch .05ch var(--acnr)
}
ytmusic-player-queue-item[play-button-state=playing], ytmusic-player-queue-item[play-button-state=loading], ytmusic-player-queue-item[play-button-state=paused] {
    background-color:rgba(0,0,0,.25)
}
/*trash
--*/
