/*ytmusic-thumbnail-renderer[thumbnail-crop_=MUSIC_THUMBNAIL_CROP_CIRCLE] .image.ytmusic-thumbnail-renderer, ytmusic-player[player-ui-state_=MINIPLAYER]*/
/*ytmusic-player[player-ui-state_=MINIPLAYER]*/
}
