css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.scarygoround.com") {

body {margin-top: 20px !important;}

noscript,
table[bgcolor="#252727"],
table[bgcolor="#d3d650"],
#footer,
table>tbody>tr:first-child,
.h2,
td[bgcolor="#e9ee85"],
img[src="site-images/notcurrentstory.png"],
table>tbody>tr>td[valign="middle"],
td[bgcolor="#ffffff"]>div:last-child,
td[colspan="2"]
 {display: none !important;}

body,
td[bgcolor="#ffffff"]
 {background: #000 !important;}

}
