css:
@-moz-document regexp('https://(?:.*\.)?etadirect.com/.*') {
  .toaGantt-tb,
  .toaGantt-tb-name,
  .rtl-text,
  .rtl-prov-name,
  .ltr-text,
  .ltr-prov-name
  {
      text-shadow: 0 0 /*[[blur]]*/em black;
      color: transparent !important;
  }

  .screenHeading,
  .screen-title,
  .toa-view-control-control,
  .toa-panel-caption,
  .toa-panel-caption__title,
  .line-call,
  .toa-button-panel button,
  .date,
  .toa-button,
  .action-link a,
  .component_visibility_readonly,
  .countdown-link,
  .countdown-time-wrapper
  {
      text-shadow: 0 0 /*[[blur]]*/em white;
      color: transparent !important;
  }

  .toaGantt-oncall-shift-name
  {
      text-shadow: 0 0 /*[[blur]]*/em #ffd600;
      color: transparent !important;    	
  }

  .search-bar-input-hint,
  .search-bar-input-hint-text
  {
      text-shadow: 0 0 /*[[blur]]*/em #686766;
      color: transparent !important;
  }

  .display-layout,
  .section-dots,
  .title-text,
  .form-item,
  .cl-static-text,
  .time_zone,
  .cl-value-wrapper,
  .cl-row,
  .action-block,
  .component,
  .visibility_readonly,
  .online-action,
  .button,
  .cl-fld-line,
  .toa-hint-field, .cap, .cap_value,
  .toa-hint-activity-header-title,
  .inline
  {
      text-shadow: 0 0 /*[[blur]]*/em #1a1816;
      color: transparent !important;
  }

  .hint_links_mobility,
  .hint_link,
  .span_link
  {
      text-shadow: 0 0 /*[[blur]]*/em #00688c;
      color: transparent !important;
  }
}
