css:
@-moz-document domain("hello.corp.ikala.tv") {
.bt_edit {
 padding-top: 0 !important;
 background-image: unset;
}

.bt_edit:hover {
  text-decoration: underline;
}

.bt_edit:before {
	width: 10px;
  content: 'Edit';
  display: inline-block;
  color: black;
  font-size: 16px;
}
	

.submenu_01, #LeftMenuContent > div {
	 transform: translateX(0%) translateY(0%);
}
 body {
	 font-family: PingFang TC;
	 font-size: 16px;
}
 .MenuItem {
	 font-family: PingFang TC;
	 font-weight: 500;
}
 #LeftMenuContent > div {
	 padding: 5px;
	 margin: 5px 3px;
	 font-family: PingFang TC;
	 background-image: unset;
	 background-color: white;
	 line-height: 16px;
}
 .submenu_01, #LeftMenuContent > div {
	 background: white;
	 color: #ccc;
	 border: 0;
	 font-size: 16px;
	 border-radius: 4px;
	 font-family: "Raleway", sans-serif;
	 transition: 0.6s;
	 overflow: hidden;
}
 .submenu_01:focus, #LeftMenuContent > div:focus {
	 outline: 0;
}
 .submenu_01:before, #LeftMenuContent > div:before {
	 content: "";
	 display: block;
	 position: absolute;
	 background: rgba(255, 255, 255, 0.5);
	 opacity: 0.5;
	 filter: blur(30px);
	 transform: translateX(-100px) skewX(-15deg);
}
 .submenu_01:after, #LeftMenuContent > div:after {
	 content: "";
	 display: block;
	 position: absolute;
	 background: rgba(255, 255, 255, 0.2);
	 top: 0;
	 opacity: 0;
	 filter: blur(5px);
	 transform: translateX(-100px) skewX(-15deg);
}
 .submenu_01:hover, #LeftMenuContent > div:hover {
	 background: #4d7fd4;
	 cursor: pointer;
	 color: white;
}
 .submenu_01:hover:before, #LeftMenuContent > div:hover:before {
	 transform: translateX(300px) skewX(-15deg);
	 opacity: 0.6;
	 transition: 0.7s;
}
 .submenu_01:hover:after, #LeftMenuContent > div:hover:after {
	 transform: translateX(300px) skewX(-15deg);
	 opacity: 1;
	 transition: 0.7s;
}
 .Menu * {
	 font-family: PingFang TC;
}

.topRight {
  width: unset;
  margin-right: 10px;
  right: 10px;
}

.topRight-item > span:nth-child(2) {
  position: relative;
  display: inline-block;
  margin-right: 15px;
}
}
