css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bash.org.ru") {

body {
width: 80%; margin: .2cm auto !important; background:#112000 url(http://i028.radikal.ru/0905/2c/5a96faf914aa.jpg) no-repeat fixed top center !important;
}

#page {
margin: auto !important; width: auto !important; text-align: center !important;
}

.header { background:url(http://s56.radikal.ru/i152/0905/ba/66a871f2e931.png) !important; font-family: 'Georgia', 'Times', 'Times New Roman', serif; font-size: 21pt; line-height: 40px; height: 40px; text-align: right; }

.header img { background:url(http://s56.radikal.ru/i152/0905/ba/66a871f2e931.png) !important; float: left; }

.menu {
margin: 0 !important; background:url(http://s56.radikal.ru/i152/0905/ba/66a871f2e931.png) !important; padding: 5px; font-family: Arial, sans-serif; text-align: left !important;
}


div#quotes p, ol li { font-size: 14px; line-height: 1.3em; }
div#quotes div.q { font-family: 'Courier New', monospace !important; font-size: 80% !important; }
div#quotes div.q div, div.stats { background:url(http://s56.radikal.ru/i152/0905/ba/66a871f2e931.png) !important; padding: 5px; border: 1px dotted #999; }
div#quotes div.q div.vote { font-family: 'Lucida Grande', 'Tahoma', sans-serif; font-size: 10pt; background:url(http://s56.radikal.ru/i152/0905/ba/66a871f2e931.png) !important; border: 0; }
div#quotes div.q div div.alr { border: none; text-align: right; }
div.news { font-family: 'Lucida Grande', 'Tahoma', sans-serif; font-size: 9pt; }
div.stats div.q { font-family: 'Monaco', 'Courier New', monospace; font-size: 8pt; font-weight: normal; }
div.news p { text-align: left; margin: 5px 0; }

a:hover {
color:#B3000F !important;
}

div.aby a { width: 50%; padding: 5px; text-align: center; display: block; margin: auto; }
div.aby a:hover { background:url(http://s56.radikal.ru/i152/0905/ba/66a871f2e931.png) !important; color: #fff; text-decoration: none; }

table {
width: auto !important; 
}

a, a:visited {
color:#3E6701 !important;
}

div#quotes div.q {
font-family: 'Courier New', monospace !important; font-size: 80% !important;
}

div#quotes div.q div, div.stats {
background:url(http://s56.radikal.ru/i152/0905/ba/66a871f2e931.png) !important; padding: 0 !important; border: none !important;
}

div#quotes div.q div.vote {
font-family: inherit !important;
}

div.q div.vote a:first-child {
font-weight: bold;
}

div.q div.vote a:first-child:before {
content: '#';
}

div.news {
background:url(http://s56.radikal.ru/i152/0905/ba/66a871f2e931.png) !important; 
font-family: 'Arial', 'Tahoma', sans-serif !important;
}

.b b {
font-weight:  normal;
}

small {
font-family: 'Times New Roman', serif;
}

div.vote {
margin-bottom: 10px !important; color: #777
}

input {
width: auto !important
}

.pager span.error { font-weight: normal; background:url(http://s56.radikal.ru/i152/0905/ba/66a871f2e931.png) !important; }
.pager a, .pager span { float: left; display: block; padding: 0 10px; margin: 0 1px; height: 20px; line-height: 20px; font-weight: normal; font-size: smaller; }
.pager a { background:url(http://s56.radikal.ru/i152/0905/ba/66a871f2e931.png) !important; }
.pager a:hover { background:url(http://s56.radikal.ru/i152/0905/ba/66a871f2e931.png) !important; color: #fff; }
.pager span { font-weight: bold; font-size: smaller; background:url(http://s56.radikal.ru/i152/0905/ba/66a871f2e931.png) !important; }
.pager span.first { margin: 0; padding: 0 10px 0 0; font-size: smaller; background:url(http://s56.radikal.ru/i152/0905/ba/66a871f2e931.png) !important; }

}
