css:
@-moz-document url-prefix("https://my.epitech.eu") {

/* Large */
body {
    background-color: /*[[bg]]*/;
    color: #eeeeee;
}

/* Card */
.mdl-card {
    background: /*[[bg_color]]*/;
    border-radius: 15px;
    border: 1px solid /*[[title_color]]*/;
}
.mdl-shadow--4dp {
    border-radius: 15px;
}
.mdl-list__item {
    color: #eeeeee;
}
.mdl-color-text--black {
    color:#eeeeee !important;
}

.mdl-color-text--primary {
    color: /*[[title_color]]*/ !important;
}
.mdl-color-text--primary-dark {
    color: /*[[title_color]]*/ !important;
}
/*Circle*/
path {
    fill:rgba(0, 0, 0, 0.4);
}

/* Title of section */
.mdl-tabs__tab {
    color: #eeeeee;
}
.mdl-tabs.is-upgraded .mdl-tabs__tab.is-active {
    color: #eeeeee;
}

}
