@-moz-document url-prefix("http://www.feedly.com/home") {
a {outline: none !important;}
a:hover, h2:hover, [class="title"]:hover, #pageTitleHint:hover, .moduleGroup h2:hover, .sourceTitle a:hover, [class="listPagePagingControl"]:hover, [class="label"]:hover, [class="listLabel"]:hover, [class="action"]:hover, [class="feedIndexTitle nonEmpty"]:hover, [class="feedIndexTitle empty"]:hover, [class="feedUnreadCount simpleUnreadCount"]:hover, [class="subscriptionDashboardTitle nonEmpty feedUnreadCount"]:hover, [class="subscriptionDashboardTitle empty"]:hover, [title="click bar to minimize"]:hover, [class="unreadCount"]:hover, [title="mark source as favorite"]:hover, [title="undo mark as favorite"]:hover, .popup span:hover, #lvcOptions div:hover, [class="checkable"]:hover, .header span:hover, .subscriptionSourcesTitle div:hover {color: #e00 !important;}
[class="unreadCount"]:hover, [class="visual"]:hover, [class="u7Entry"]:hover, .shareBar img:hover, [class="youtubeThumbnailHolder"]:hover, [class="flickImageHolder"]:hover, #ad:hover {border-color: #dd0000 !important;}
#feedlyFrame {
width: 980px !important;
margin: 0 !important;
}


/* Top */
#feedlyFrameHeaderContent {width: 980px !important;}
#merlin_results > DIV {width: 100% !important;}
#merlin_sliders {width: 777px !important;}
.source {width: 24% !important;}


/* Tab */
#feedlyTabs {
position: relative !important;
max-width: 76px !important;
}
.label {
text-align: right !important;
text-transform: none !important;
}

/* Main */
#mainArea {margin: 0 !important;}
#feedlyCenter, #feedlyPage{width: 100% !important;}
#feedlyCenter{margin: 0 !important;}
#feedlyPart0 {
position: relative !important;
margin-left: 90px !important;
}
#warningMessage {width: 709px !important;}
#searchBox {
border-left: 1px solid #dadada !important;
border-right: 1px solid #dadada !important;
}
h1 {
font-size: 1.5em !important;
text-transform: none !important;
}
#pageTitleHint:hover {color: #808080 !important;}
[onclick*="this.$page().markCategoryAsRead"]:hover, [class="unreadCount"]:hover {text-decoration: line-through !important;}
h2 {
display: inline !important;
font-size: 1.1em !important;
text-transform: none !important;
}
.favicon {
display: inline !important;
cursor: pointer !important;
opacity: 0.9 !important;
}
.summary {
font-size: 0.9em !important;
cursor: pointer !important;
}

/* inlinedFrame */
.content {
line-height: 1.6 !important;
font-size: 0.9em !important;
}
.entryBody img {height: 100% !important;}
embed {min-height: 100% !important;}
.commentsHolder h2 {
color: #74ab00 !important;
cursor: auto !important;
}
.tweet, .ffEntry {font-size: 0.85em !important;}


/* Preview */
#quicklookFramePart {
margin-top: -30px !important;
-moz-box-shadow: 0px 1px 2px #666;
}
#quicklookActions {cursor: auto !important;}


/* Side Area */
#sideArea {
position: relative !important;
width: 176px !important;
margin: 0 !important;
}
.sideAreaModule {max-width: 141px !important;}
.sideAreaModule h2 {
font-size: 1em !important;
cursor: auto !important;
}
.feedIndexTitle .favicon {display: none !important;}
.moduleGroup {margin-bottom: 20px !important;}
.moduleGroup h2 {cursor: pointer !important;}
.feedIndex {
overflow: hidden !important;
width: 140px !important;
cursor: auto !important;
}
.feedIndexTitle.empty, .feedIndexTitle.nonEmpty {
display: inline !important;
font-size: 0.9em !important;
cursor: pointer !important;
}
.feedUnreadCount {
margin: 1px 0 0 10px !important;
font: 0.9em Arial !important;
}
#fusionModule_part, #discoverModule_part, #sponsorsModule_part, .nbrRecommendations, #topReadersModule_part {display: none !important;}


/* cover, popular */
#headlines1 > div, #headlines2 > div {height: 220px !important;}
.cell {
float: none !important;
width: 204px !important;
}
.cell2 {
width: 587px !important;
margin: -1320px 0 0 234px !important;
}
.cell2 .u4Entry {
position: relative !important;
overflow: hidden !important;
width: 100% !important;
height: 122px !important;
margin-bottom: 30px !important;
}
.cell2 .summary {
position: relative !important;
overflow: visible !important;
top: 18px !important;
}
.cell2 .u4Entry .metadata {
position: absolute !important;
display: inline !important;
top: 0 !important;
margin-top: 18px !important;
}
#sectionsArea .cell {float: left !important;}
.u15EntryList {margin: 10px 0 20px !important;}
#headlines1 > DIV:last-child, #headlines2 > DIV:last-child, #headlines1 .summary, #headlines2 .summary {display: none !important;}

/* what's new?, Magazine */
h2.topH2, #main_shows h2, #main_photos h2 {
color: #74ab00 !important;
cursor: auto !important;
}
.u4Entry .title {font-size: 1.2em !important;}
#topArea, #featuredArea, #featured_entries, #headline {margin-top: 20px !important;}
.u12Entry {margin-bottom: 2px !important;}
.u12Entry .title {font-size: 1em !important;}
.entryList {
margin-top: 20px !important;
margin-bottom: 20px !important;
}


/* Titles Only */
.u1Entry {cursor: pointer !important;}
.u1Entry .title {font-size: 0.9em !important;}
.u1Entry .title.read {font-weight: normal !important;}


/* Entire Content Inlined */
.u100EntryList .u100Entry img, .u100EntryList .u100Entry embed, .u100EntryList .u100Entry pre, .u100EntryList .u100Entry code {max-width: 100% !important;}


/* Search */
.indentedContent {margin: 20px 0 30px !important;}
#newsArea_content .newsItem {
overflow: hidden !important;
max-height: 82px !important;
margin-bottom: 20px !important;
font-size: 1.2em !important;
}
.newsItem {margin-bottom: 2px !important;}
.newsItem .title {color: #a00 !important;}
.newsItem .title:hover {color: #e00 !important;}
.source {color: #075fb2 !important;}
.newsItem .summary {
display: block !important;
font-size: 0.85em !important;
cursor: auto !important;
}
.visual {display: block !important;}
#crunchbaseArea, #nytArea, #financeModule_part {display: none !important;}


/* Dashboard */
.postIt {margin-bottom: 200px !important;}


/* Popup */
#popupPart {
cursor: auto !important;
opacity: 0.9 !important;
}
[class="checkable"] {display: block !important;}
.section .label {
color: #333333 !important;
cursor: auto !important;
}
.marker-bubble-popup {display: none !important;}

/* Sign */
#feedlySignPart {font: normal 1em Arial !important;}
.signAlert, #feedlySignPart .action {font-size: 1.2em !important;}
I {
font-style: normal !important;
font-weight: bold !important;
}


/* Button */
