css:
/** размер шрифта **/
body, td {
  font-size: 14px !important;
  font-family: Verdana, Arial, Helvetica, sans-serif !important;
}
.small {
  font-size: 12px !important;
}

/** отображение описания в списках **/
td.content br[clear="all"] + span.small {
  display:none;
}

/** ссылка на редактирование **/
td.content a + a, td.content i + a {
  color: #888;
}

/** пользователь в карточке задачи **/
td[align="left"][colspan="3"] a,
table[id~="atasks"] td.content a + a,
table[id~="atasks"] td.content i + a {
  color: #4778DE;
}
/** пользователи в списке **/
td.content a img + b {
  font-size: 11px;
  font-weight: normal;
  color:#4778DE;
  display:inline-block;
  overflow: hidden;
  min-width: 90px;
  width: 80%;
  white-space: nowrap;
}


/** пользователи в проекте **/
table[cellspacing="1"][cellpadding="3"] td.content a img + b {
  font-size: 12px;
  font-weight: normal;
  display:inline;
  overflow: visible;
  white-space: normal;
}

/** картинка проекта и людей в списке **/
td[align="left"][class="content"] a > img {
  display: none;
}

/** картинка удаления связи **/
td[align="left"][class="content"] a > img[alt="Убрать связь"] {
  display: block;
}

/** дата в списке **/
td.content > span[style="padding-left: 5px; padding-right: 5px;"] {
  font-size: 11px;
  overflow: hidden;
  display:inline-block;
  white-space: nowrap;
  width: 80px;
}
			
/** лишние пункты меню **/
/*#menuHosting, #menuLastProjects {
  display:none;
}*/

/** картинка лето **/
img[src="/files/leto.jpg"] {
  display:none;
}

/** иконка удаления у фильтров **/
#menuFilters img[src="/images/icons/no.gif"] {
  display:none;
}

/** пункты меню **/
td.menu a.control {
  overflow: hidden;
  display:inline-block;
  white-space: nowrap;
  width: 185px;
  text-decoration: none !important;
}

table#menuFilters td.menu a.control {
  width: 170px;
  white-space: normal;
  line-height: 100%;
  font-size: 12px !important;
  margin: 0 0 2px 0;
}

table#menuFilters a {
  float: left;
  display: inline-block;
}

table#menuFilters td.menu {
  padding: 0;
}

/** события в списке комментов**/
p[style="background-color:#e8e8e8;padding-bottom:4px;"] {
  font-size: 70%;
  color: #555;
  overflow: hidden;
  display: block;
  height: 13px;
  cursor: pointer;
}
p[style="background-color:#e8e8e8;padding-bottom:4px;"]:hover {
  overflow: visible;
  height: 40px;
  cursor: pointer;
}

/** раскарсим серый */
td.head {
  background-color: #CEE3F6 !important;
}
td.menu {
  background-color: #EFFDFD !important;
}
table.framed {
  background-color:#CEE3F6 !important;
}

/*table#atasks td[class="content"][colspan="3"] {
  white-space: nowrap;
  overflow: hidden;
  display: block;
  width: 600px;
}*/

@media print {
  body>table>tbody>tr>td[width="200"] {
    display: none;
  }
}

#worked_time_otchet, #task_timer {
  white-space: nowrap;
}

/* отключаю шапку*/
/*tr.headTr td {
  display: none;
}*/

/* дурацкий msgbox */
/*div#msgbox {
    display: none !important;
}*/

/** Патчим напоминалку */
#zarplata {
overflow: hidden;
width: 155px;
white-space: nowrap;
cursor: pointer;
color: #ccc;
}
#zarplata:hover {
overflow: auto;
width: auto;
white-space: normal;
background: #ccc;
padding: 3px;
cursor: pointer;
color: #000;
}

/** убираем части шапки*/
a.helpButton, a.logo img {
display: none;
}
tr.headTr h1 {
display: none;
}
tr.headTr td {
padding: 2px;
}
